return items.map(item => {
  let text = item.json.output;

  // Remove code fences like ```json ... ```
  text = text.replace(/```json|```/g, "").trim();

  let parsed = {};
  try {
    parsed = JSON.parse(text);
  } catch (err) {
    parsed = { error: "Failed to parse JSON", raw: text };
  }

  return { json: parsed };
});
