function obtenerPrioridad(priority) {
  if (priority >= 1000000) return { emoji: 'üî¥', texto: 'Highest' }; // Prioridad m√°xima
  if (priority >= 750000)  return { emoji: 'üü†', texto: 'High' };    // Prioridad alta
  if (priority >= 500000)  return { emoji: 'üîµ', texto: 'Normal' };  // Prioridad normal
  if (priority >= 250000)  return { emoji: 'üü¢', texto: 'Low' };     // Prioridad baja
  return { emoji: 'üö´', texto: 'Ignore' };                           // Ignorado o desactivado
}

// 3Ô∏è‚É£ Recorremos cada intent y lo convertimos en un item compatible con n8n.
// Cada item incluir√°:
//   - Nombre: El displayName del intent
//   - prioridad: Valor num√©rico de la prioridad del intent
//   - colorPrioridad: Emoji que representa la prioridad
//   - textoPrioridad: Descripci√≥n textual de la prioridad
const intents = data.map(intent => {

  // Extrae la informaci√≥n de prioridad del intent actual.
