  const prioridadInfo = obtenerPrioridad(intent.priority || 0);

  // Construye el item final que n8n espera: { json: {...} }
  return {
    json: {
      Nombre: intent.displayName,
      prioridad: intent.priority,
      colorPrioridad: prioridadInfo.emoji,
      textoPrioridad: prioridadInfo.texto
    }
  };
});

