// Extract pages from OCR results and format for processing
const pages = items[0].json.pages || [];
const output = [];

for (const page of pages) {
  // page.index is your page number (0-based). Add +1 for 1-based numbering
  const pageNumber = page.index+1;
  const content    = page.markdown; // Extract markdown content
  const images     = page.images;

  output.push({
    json: { pageNumber, content },
  });
}

return output;

