// Get the input data
const inputData = $input.first().json;

// Try to find the data array and parameters
let data, parameters;

// Check if inputData is the array directly
if (Array.isArray(inputData)) {
  // If inputData is an array, look for the object with data property
  const dataObj = inputData.find(item => item.data);
  if (dataObj) {
    data = dataObj.data;
    parameters = dataObj.parameters;
  }
} else if (inputData.data) {
  // If inputData is an object with data property
  data = inputData.data;
  parameters = inputData.parameters;
}

// If we still don't have data, return an error
if (!data || !Array.isArray(data)) {
  return [{ json: { error: "Could not find data array", inputStructure: Object.keys(inputData) } }];
}

// Helper function to extract emails as comma-separated string
function extractEmails(emailsArray) {
  if (!emailsArray || !Array.isArray(emailsArray) || emailsArray.length === 0) {
    return null;
  }
  return emailsArray.join(', ');
}

// Process each business
const outputData = data.map(business => ({
  // Search parameters
  query: parameters?.query || null,
  language: parameters?.language || null,
  lat: parameters?.lat || null,
  lon: parameters?.lng || parameters?.lon || null,
  zoom: parameters?.zoom || null,
  limit: parameters?.limit || null,

  // Business data
  business_id: business.business_id || null,
  place_id: business.place_id || null,
  phone_number: business.phone_number || null,
  name: business.name || null,
  full_address: business.full_address || null,
  review_count: business.review_count || null,
  rating: business.rating || null,
  website: business.website || null,
  verified: business.verified || null,
  type: business.type || null,
  reviews_link: business.reviews_link || null,
  place_link: business.place_link || null,
  about_summary: business.about?.summary || null,
  address: business.address || null,
  price_level: business.price_level || null,
  district: business.district || null,
  street_address: business.street_address || null,
  city: business.city || null,
  zipcode: business.zipcode || null,
  state: business.state || null,
  country: business.country || null,

  // Extract emails and social media contacts
  email: extractEmails(business.emails_and_contacts?.emails),
  linkedin: business.emails_and_contacts?.linkedin || null,
  instagram: business.emails_and_contacts?.instagram || null,

  // Additional social media fields
  facebook: business.emails_and_contacts?.facebook || null,
  twitter: business.emails_and_contacts?.twitter || null,
  youtube: business.emails_and_contacts?.youtube || null,
  tiktok: business.emails_and_contacts?.tiktok || null,
  pinterest: business.emails_and_contacts?.pinterest || null,
  snapchat: business.emails_and_contacts?.snapchat || null,
  github: business.emails_and_contacts?.github || null,
  yelp: business.emails_and_contacts?.yelp || null,

  // Additional phone numbers from emails_and_contacts
  additional_phones: business.emails_and_contacts?.phone_numbers?.length > 0 
    ? business.emails_and_contacts.phone_numbers.join(', ') 
    : null
}));

// Return the processed data
return outputData.map(item => ({ json: item }));
