// Get input data from Parser node
const inputData = $input.all()[0].json.output || {};

// Default values if data is incomplete
const store = inputData.store || { store_name: 'not available', store_location: 'not available' };
const transaction = inputData.transaction || { date: 'not available', time: 'not available' };
const items = inputData.items || [];
const summary = inputData.summary || { total: 'not available', payment_method: 'not available', expense_category: 'not available' };

// Create message with available information
let message = '';

if (store.store_name !== 'not available') {
  message += `📋 Store: ${store.store_name}\n`;
}
if (store.store_location !== 'not available') {
  message += `📍 Location: ${store.store_location}\n`;
}
if (transaction.date !== 'not available' || transaction.time !== 'not available') {
  message += `📅 Date: ${transaction.date} ${transaction.time}\n`;
}

if (items.length > 0) {
  message += `\n🛒 Items:\n`;
  items.forEach(item => {
    message += `- ${item.description || 'not available'}: ${item.total_price || 'not available'}\n`;
  });
}

if (summary.total !== 'not available') {
  if (parseFloat(summary.total) === 0) {
    message = 'Looks like an input error, total is 0? Did you get this for free? Please check again.';
  } else {
    message += `\n💸 Total: Rp ${summary.total}\n`;
  }
}

if (summary.expense_category !== 'not available' && parseFloat(summary.total) !== 0) {
  message += `📌 Category: ${summary.expense_category}\n`;
}

// Default message if no information is available
if (!message) {
  message = 'No information available from the input.';
}

// Return message as output
return [
  {
    json: {
      message: message
    }
  }
];

