const results = items[0].json.local_results || [];

const parsedResults = results.map(entry => {
  // Remove "/url?q=" if present in the website
  let cleanWebsite = entry.website || null;
  if (cleanWebsite && cleanWebsite.startsWith("/url?q=")) {
    cleanWebsite = cleanWebsite.replace("/url?q=", "").split("&")[0];
  }

  return {
    title: entry.title || null,
    website: cleanWebsite,
    address: entry.address || null,
    phone: entry.phone || null,
  };
});

// Return each result as a separate item
return parsedResults.map(result => ({ json: result }));
