// Convert Excel serial date -> YYYY-MM-DD
const base = new Date(Date.UTC(1899, 11, 30));
function fromSerial(s) {
  if (!s || isNaN(Number(s))) return s; // skip if not a number
  const ms = Number(s) * 86400 * 1000;
  return new Date(base.getTime() + ms).toISOString().slice(0, 10);
}

return items.map(item => {
  let row = { ...item.json };

  // Convert possible date fields
  if (row.__EMPTY && !isNaN(row.__EMPTY)) {
    row.__EMPTY = fromSerial(row.__EMPTY);
  }
  if (row.__EMPTY_1 && !isNaN(row.__EMPTY_1) && row[" Contractor Timesheet"]?.includes("Weekending")) {
    row.__EMPTY_1 = fromSerial(row.__EMPTY_1);
  }

  return { json: row };
});

// input[0].json contains your JSON
const outputString = $json["output"];

// Parse the string
const parsedJSON = JSON.parse(outputString);

// Return as n8n items
return parsedJSON.map(item => ({ json: item }));

const data = $items()[0].json;

let html = `
<h2>Timesheet Verification</h2>
<p>Contractor: ${data.document_info.contractor_name}<br>
Client: ${data.document_info.client_name}<br>
Manager: ${data.document_info.manager_name}<br>
End Date: ${data.document_info.end_date}<br>
Total Hours: ${data.document_info.total_hours}</p>

<table border="1" cellpadding="5" cellspacing="0">
<tr>
<th>Day</th>
<th>Date</th>
<th>Hours</th>
<th>Description</th>
</tr>
`;

data.time_entries.forEach(entry => {
  // Safely handle null or empty description
  const desc = entry.description ? entry.description.replace(/\n/g, '<br>') : '';

  html += `
<tr>
<td>${entry.day_of_week}</td>
<td>${entry.date}</td>
<td>${entry.hours_worked}</td>
<td>${desc}</td>
</tr>`;
});

html += `</table>`;

return [{ json: { email_body: html } }];
