// In a Function node in n8n
const inputData = $input.all();

function cleanAllPosts(data) {
 return data.map(item => {
 try {
 // Check if item exists and has the expected structure
 if (!item || typeof item !== 'object') {
 return { cleaned_text: '', error: 'Invalid item structure' };
 }

 // Get the text, with multiple fallbacks
 let text = '';
 if (typeof item === 'string') {
 text = item;
 } else if (item.json && item.json.text) {
 text = item.json.text;
 } else if (typeof item.json === 'string') {
 text = item.json;
 } else {
 text = JSON.stringify(item);
 }

 // Make sure text is a string
 text = String(text);

 // Perform the cleaning operations
 try {
 text = text.replace(/&#x2F;/g, '/');
 text = text.replace(/&#x27;/g, "'");
 text = text.replace(/&\w+;/g, ' ');
 text = text.replace(/<[^>]*>/g, '');
 text = text.replace(/\|\s*/g, '| ');
 text = text.replace(/\s+/g, ' ');
 text = text.replace(/\s*(https?:\/\/[^\s]+)\s*/g, '\n$1\n');
 text = text.replace(/\n{3,}/g, '\n\n');
 text = text.trim();
 } catch (cleaningError) {
 console.log('Error during text cleaning:', cleaningError);
 // Return original text if cleaning fails
 return { cleaned_text: text, warning: 'Partial cleaning applied' };
 }

 return { cleaned_text: text };

 } catch (error) {
 console.log('Error processing item:', error);
 return { 
 cleaned_text: '', 
 error: `Processing error: ${error.message}`,
 original: item
 };
 }
 }).filter(result => result.cleaned_text || result.error); 
}

try {
 return cleanAllPosts(inputData);
} catch (error) {
 console.log('Fatal error:', error);
 return [{ 
 cleaned_text: '', 
 error: `Fatal error: ${error.message}`,
 input: inputData 
 }];
}

function convertSalaryRangeToNumber(salaryStr) {
  if (!salaryStr) return null;

  // Remove $ and spaces, convert to lowercase
  salaryStr = salaryStr.replace(/\$/g, '').replace(/\s/g, '').toLowerCase();

  // Replace 'k' with '000'
  salaryStr = salaryStr.replace(/k/g, '000');

  // Split by dash, en dash, em dash
  const parts = salaryStr.split(/[-‚Äì‚Äî]/);

  // Extract numbers
  const numbers = parts.map(part => {
    // Remove non-digit/non-dot characters
    const cleaned = part.replace(/[^\d.]/g, '');
    return cleaned ? parseFloat(cleaned) : NaN;
  }).filter(num => !isNaN(num));

  if (numbers.length === 0) return null;
  if (numbers.length === 1) return numbers[0];
  if (numbers.length === 2) return (numbers[0] + numbers[1]) / 2;

  // fallback if more than 2 numbers (rare)
  return numbers[0];
}

// Example usage inside n8n Function node
// Assume input data is in items, and salary is in item.json.salary
for (let item of items) {
  const salaryStr = item.json.salary;
  item.json.salary_numeric = convertSalaryRangeToNumber(salaryStr);
}

return items;
