return items.map(item => {
  const maxLength = 13000;

  const truncatedContent = (item.json.content || "").slice(0, maxLength);
  const truncatedSnippet = (item.json.contentSnippet || "").slice(0, maxLength);

  return {
    json: {
      ...item.json,
      content: truncatedContent,
      contentSnippet: truncatedSnippet,
    },
  };
});
