// n8n Code node (JavaScript)
const items = $input.all().map(item => item.json);

const grouped = {};

for (const item of items) {
  const companyId = item.companies_id;
  if (!grouped[companyId]) {
    grouped[companyId] = [];
  }
  grouped[companyId].push(item.email_status);
}

// Build array of objects for n8n update nodes
const result = Object.entries(grouped).map(([companyId, statuses]) => ({
  companies_id: Number(companyId),
  email_status: statuses.every(status => status === "risky") ? "risky" : "valid"
}));

return result.map(obj => ({ json: obj }));
