const results = [];

for (const item of $input.all()) {
  const data = item.json.output; // This is the string field to be parsed

  // Check if data is a valid string before trying to parse
  if (typeof data === 'string' && data.trim() !== '') {
    try {
      const obj = JSON.parse(data); // Attempt to parse the string into a JSON object
      results.push({ json: obj });   // If successful, add it to the results
    } catch (e) {
      // If parsing fails, this block catches the error.
      // We do nothing, effectively skipping the invalid item.
    }
  }
}

return results;
