parameters.text:
={{ 
`ANALYZE CV AND FIND JOBS:

CV Content:
${$json.cvData}

User Preferences:
- Location: ${$json.location}
- Work Setting: ${$json.workSetting}
- Job Type: ${$json.jobType}
- Salary: ${$json.salaryExpectation}
- Additional: ${$json.additionalPreferences}

TASKS:
1. Extract key info from CV (name, skills, experience, achievements)
2. Search for 3 best matching jobs using tools
3. Generate personalized cover letter for each job
4. Format everything as structured JSON for email template`
}}

parameters.jsCode:
let raw = $json.output || "";

// Clean markdown fences if they exist
raw = raw.replace(/```json/g, '').replace(/```/g, '').trim();

let parsed;
try {
  parsed = JSON.parse(raw);
} catch (e) {
  parsed = { error: "Failed to parse JSON", raw };
}

return parsed;
