assignments.value:
={{
  // Educational keywords (higher scores)
  (($json.Title.toLowerCase().includes('tutorial')) ? 15 : 0) +
  (($json.Title.toLowerCase().includes('course')) ? 15 : 0) +
  (($json.Title.toLowerCase().includes('guide')) ? 12 : 0) +
  (($json.Title.toLowerCase().includes('explained')) ? 12 : 0) +
  (($json.Title.toLowerCase().includes('beginner')) ? 10 : 0) +
  (($json.Title.toLowerCase().includes('step by step')) ? 15 : 0) +
  (($json.Title.toLowerCase().includes('complete')) ? 10 : 0) +

  // AI-specific terms
  (($json.Title.toLowerCase().includes('prompt engineering')) ? 20 : 0) +
  (($json.Title.toLowerCase().includes('ai agent')) ? 18 : 0) +
  (($json.Title.toLowerCase().includes('ai tools')) ? 15 : 0) +
  (($json.Title.toLowerCase().includes('prompting')) ? 15 : 0) +

  // Quality indicators
  ((parseInt($json.Views) > 100000) ? 10 : 0) +
  ((parseInt($json.Views) > 50000) ? 5 : 0) +
  ((parseInt($json.Likes) > 1000) ? 8 : 0) +

  // Penalty for monetization indicators
  (($json.Title.toLowerCase().includes('money') || 
    $json.Title.toLowerCase().includes('rich') || 
    $json.Title.toLowerCase().includes('earn')) ? -20 : 0)
}}
