// Check if menu data has changed since last check
const currentData = $input.all();
const lastCheckKey = 'last_menu_check';

// Get stored last check data from workflow static data
const lastCheckData = $getWorkflowStaticData('global')[lastCheckKey] || null;

// Convert current data to string for comparison
const currentDataString = JSON.stringify(currentData);

// Initialize result
let hasChanged = false;
let newItems = [];
let updatedItems = [];
let removedItems = [];

if (lastCheckData) {
  // Compare with previous data
  if (currentDataString !== lastCheckData.dataString) {
    hasChanged = true;

    // Detailed comparison logic
    const previousItems = JSON.parse(lastCheckData.dataString);
    const currentItems = currentData;

    // Find new items (compare by Item ID)
    newItems = currentItems.filter(current => 
      !previousItems.some(prev => prev['Item ID'] === current['Item ID'])
    );

    // Find updated items (same ID but different content)
    updatedItems = currentItems.filter(current => {
      const prevItem = previousItems.find(prev => prev['Item ID'] === current['Item ID']);
      return prevItem && JSON.stringify(prevItem) !== JSON.stringify(current);
    });

    // Find removed items (existed before but not now)
    removedItems = previousItems.filter(prev => 
      !currentItems.some(current => current['Item ID'] === prev['Item ID'])
    );
  }
} else {
  // First run - all items are considered new
  hasChanged = true;
  newItems = currentData;
}

// Store current data for next comparison
$getWorkflowStaticData('global')[lastCheckKey] = {
  dataString: currentDataString,
  timestamp: new Date().toISOString()
};

// Return results
return [{
  json: {
    hasChanged,
    newItems,
    updatedItems,
    removedItems,
    totalItems: currentData.length,
    checkTime: new Date().toISOString()
  }
}];

