const airtableProductName = $('Update Quickbooks and Stripe Customer Ids').item.json.fields['Quickbooks Product Name'];
const quantity = $('Update Quickbooks and Stripe Customer Ids').item.json.fields.Quantity;

const quickBooksProducts = $json.QueryResponse.Item;

const matchingProduct = quickBooksProducts.find(product => product.Name === airtableProductName);

if (matchingProduct) {
  const unitPrice = matchingProduct.UnitPrice || 0;
  const amount = unitPrice * (quantity || 0);

  return {
    json: {
      Id: matchingProduct.Id,
      Description: matchingProduct.Description,
      Amount: amount,
    }
  };
} else {
  return {
    json: {
      Id: null,
      Description: null,
      Amount: 0,
    }
  };
}
