const result = $json["tasks"]?.[0]?.result?.[0] || {};
const output = [];

let hasSnippet = false;

if (result.items) {
  for (const item of result.items) {
    if (item.type === 'featured_snippet') {
      output.push({ json: {
        Keyword: result.keyword,
        "Snippet Title": item.title,
        URL: item.url,
        Domain: item.domain,
        "Snippet Text": item.description ? item.description.substring(0, 200) : ''
      }});
      hasSnippet = true;
      break;
    }
  }
}

if (!hasSnippet) {
  output.push({ json: {
    Keyword: result.keyword,
    "Snippet Title": '',
    URL: '',
    Domain: '',
    "Snippet Text": ''
  }});
}

return output;

const result = $json["tasks"]?.[0]?.result?.[0];
const features = {
  Keyword: result?.keyword || "",
  Featured_Snippet: false,
  People_Also_Ask: false,
  Local_Pack: false,
  Video: false,
  Image_Pack: false,
  Top_Stories: false,
  Knowledge_Panel: false,
  Shopping_Ads: false
};

if (result?.items) {
  for (const item of result.items) {
    const type = item.type?.toLowerCase();
    if (type.includes("featured_snippet")) features.Featured_Snippet = true;
    if (type.includes("people_also_ask")) features.People_Also_Ask = true;
    if (type.includes("local") || type.includes("map")) features.Local_Pack = true;
    if (type.includes("video")) features.Video = true;
    if (type.includes("image")) features.Image_Pack = true;
    if (type.includes("top_stories") || type.includes("news")) features.Top_Stories = true;
    if (type.includes("knowledge")) features.Knowledge_Panel = true;
    if (type.includes("shopping")) features.Shopping_Ads = true;
  }
}

return [{ json: features }];

const result = $json["tasks"][0]["result"][0];
const output = [];

if (result.items) {
  for (const item of result.items) {
    if (item.type === 'organic') {
      output.push({
        json: {
          Keyword: result.keyword,
          Rank: item.rank_group,
          Position: item.rank_absolute,
          Title: item.title || '',
          URL: item.url || '',
          Domain: item.domain || '',
          Breadcrumb: item.breadcrumb || '',
          Snippet: item.description || ''
        }
      });
    }
  }
}

return output;

const data = $json["tasks"][0]["result"][0];
let topDomain = null;
if (data.items) {
  for (const item of data.items) {
    if (item.type === 'organic') {
      topDomain = item.domain;
      break;
    }
  }
}
return {
  json: {
    Keyword: data.keyword,
    top_domain: topDomain
  }
};

const result = $json.tasks?.[0]?.result?.[0];
const output = [];
if (result && result.items) {
  for (const biz of result.items) {
    if (biz.type === 'local_pack' || biz.type === 'map') {
      output.push({ json: {
        Keyword: result.keyword || ($json.Keyword || ''),
        "Business Name": biz.title || '',
        Details: biz.description || ''
      } });
    }
  }
}
return output;

const tasks = $json.tasks;
const output = [];

if (tasks && tasks[0] && tasks[0].result) {
  const suggestions = tasks[0].result;
  const keyword = tasks[0].data.keywords ? tasks[0].data.keywords[0] : ($json.Keyword || '');

  for (const sug of suggestions) {
    output.push({
      Keyword: keyword,
      "Suggested Keyword": sug.keyword,
      "Search Volume": sug.search_volume || 0,
      CPC: sug.cpc || 0,
      Competition: sug.competition || sug.competition_level || ''
    });
  }
}

return output;

const items = $json.tasks[0].result[0].items || [];
let allRelated = [];

items.forEach(item => {
  const mainKeyword = item.keyword_data?.keyword || null;
  const related = item.related_keywords || [];

  related.forEach(keyword => {
    allRelated.push({
      MainKeyword: mainKeyword,
      RelatedKeyword: keyword
    });
  });
});

return allRelated;

const topic = $input.first().json.tasks[0].data.topic || '';
const subs = $json.tasks[0].result[0].sub_topics || [];

return subs.map((s, idx) => ({
  Keyword: topic,
  Subtopic: s,
  Rank: idx + 1
}));

const kw = $json.tasks[0].result[0].seed_keywords?.[0] || $json.Keyword || '';
const items = $json.tasks[0].result || [];
const out = [];

items.forEach(res => {
  (res.items || []).forEach(i => {
    out.push({
      Keyword: kw,
      IdeaKeyword: i.keyword,
      SV: i.search_volume || null,
      CPC: i.cpc || null,
      Competition: i.competition || null
    });
  });
});

return out;

const keyword = $json.tasks?.[0]?.result?.[0]?.keyword || '';
const monthly = $json.tasks?.[0]?.result?.[0]?.monthly_searches || [];
const defaultCpc = $json.tasks?.[0]?.result?.[0]?.cpc || 0;

return monthly.map(m => ({
  Keyword: keyword,
  Year: m.year,
  Month: m.month,
  Searches: m.search_volume,
  CPC: m.cpc !== undefined ? m.cpc : defaultCpc
}));

const kw = $json.keyword || $json.Keyword || ($json.tasks && $json.tasks[0].keyword) || $json.tasks?.[0]?.result?.[0]?.keyword;
const topItems = $json.tasks?.[0]?.result?.[0]?.items || [];
const out = [];

topItems.forEach(section => {
  // ‚úÖ PAS: People Also Search op hoofdniveau
  if (section.type === 'people_also_search_element') {
    out.push({
      Type: "PAS",
      Keyword: kw,
      Related: section.keyword,
      Rank: section.rank_group || section.rank
    });
  }

  // ‚úÖ Geneste PAA: People Also Ask elementen
  if (Array.isArray(section.items)) {
    section.items.forEach(i => {
      if (i.type === 'people_also_ask_element') {
        out.push({
          Type: "PAA",
          Keyword: kw,
          Question: i.title
        });
      }
    });
  }
});

return out;

const items = $input.all();

let totalSearches = 0;
let totalCPC = 0;
let cpcCount = 0;

for (const item of items) {
  const searches = item.json.Searches || 0;
  const cpc = item.json.CPC;

  totalSearches += searches;

  if (typeof cpc === 'number') {
    totalCPC += cpc;
    cpcCount++;
  }
}

const avgSearches = totalSearches / items.length;
const avgCPC = cpcCount > 0 ? totalCPC / cpcCount : null;

return [
  {
    json: {
      Keyword: items[0].json.Keyword,
      AverageSearchVolume: Math.round(avgSearches),
      AverageCPC: avgCPC !== null ? Math.round(avgCPC * 100) / 100 : null,
      DataPoints: items.length
    }
  }
];

const output = [];

for (const item of $input.all()) {
  const hasSnippet = item.json["Snippet Title"] && item.json["Snippet Title"].trim() !== "";

  output.push({
    json: {
      Keyword: item.json.Keyword,
      HasSnippet: hasSnippet ? "Yes" : "No"
    }
  });
}

return output;

const item = $input.first().json;

const serpFeatures = [
  'Featured_Snippet',
  'People_Also_Ask',
  'Local_Pack',
  'Video',
  'Image_Pack',
  'Top_Stories',
  'Knowledge_Panel',
  'Shopping_Ads'
];

const found = serpFeatures.filter(key => item[key]);

return [{
  json: {
    Keyword: item.Keyword,
    Present_SERP_Features: found.join(', ') || 'None'
  }
}];

const items = $input.all();
let total = 0;
let count = 0;

const mapCompetition = {
  LOW: 1,
  MEDIUM: 2,
  HIGH: 3
};

let keyword = items[0]?.json?.Keyword || "UNKNOWN";

let avgLabel = "UNKNOWN";

for (const item of items) {
  const raw = item.json.Competition?.toUpperCase();
  const value = mapCompetition[raw];
  if (value) {
    total += value;
    count++;
  }
}

if (count > 0) {
  const avg = total / count;
  if (avg < 1.5) {
    avgLabel = "LOW";
  } else if (avg < 2.5) {
    avgLabel = "MEDIUM";
  } else {
    avgLabel = "HIGH";
  }
}

return [{
  json: {
    Keyword: keyword,
    Average_Competition: avgLabel
  }
}];

const items = $input.all();

if (items.length === 0) {
  return [];
}

// Neem alleen de eerste item (aangenomen dat het type PAA is)
const first = items.find(i => i.json.Type === 'PAA');

return [{
  json: {
    Keyword: first?.json.Keyword || "UNKNOWN",
    Question: first?.json.Question || "No question found"
  }
}];

const items = $input.all();

if (items.length === 0) {
  return [];
}

// Neem alleen de eerste relevante entry
const first = items[0].json;

return [{
  json: {
    Keyword: first.Keyword || "UNKNOWN",
    Business_Name: first["Business Name"] || "No name found",
    Details: first.Details || "No details"
  }
}];

const inputs = $input.all();
const combined = {};

for (const item of inputs) {
  Object.assign(combined, item.json);
}

return [{
  json: {
    Keyword: combined.Keyword || null,
    Avg_Search_Volume: combined.AverageSearchVolume || null,
    Average_CPC: combined.AverageCPC || null,
    DataPoints: combined.DataPoints || null,
    Has_Snippet: combined.HasSnippet || null,
    SERP_Features: combined.Present_SERP_Features || null,
    Top_Organic_Domain: combined.Domain || null,
    PAA_Question: combined.Question || null,
    Screenshot_URL: combined.tasks?.[0]?.result?.[0]?.url || null,
    Backlinks: combined.Backlinks || null,
    Referring_Domains: combined['Referring Domains'] || null,
    Spam_Score: combined['Backlinks Spam Score'] || null,
    Country: combined.Country || null,
    Platform: combined.Platform || null,
    Local_Business_Name: combined.Business_Name || null,
    Local_Details: combined.Details || null,
    Average_Competition: combined.Average_Competition || null
  }
}];
