parameters.jsCode:
// Parse Gemini response and extract content
const response = $input.first().json;
let content = {};

try {
  // Gemini API response structure
  const text = response.candidates[0].content.parts[0].text;
  content = JSON.parse(text);
} catch (e) {
  // Fallback content if parsing fails
  content = {
    topic: 'AI Innovation 2025',
    facebook: 'üöÄ Discover how AI is transforming businesses in 2025. From automation to insights, the future is here!',
    instagram: '‚ú® AI Revolution Alert! ü§ñ Swipe to see how artificial intelligence is changing everything we know about tech üí°',
    tiktok: 'POV: AI just made your job 10x easier ü§Ø #AITech #FutureTech',
    linkedin: 'The convergence of AI and enterprise technology is creating unprecedented opportunities for innovation.',
    hashtags: '#AI #Innovation #TechTrends #DigitalTransformation #FutureTech',
    bestTime: '10:00 AM EST'
  };
}

// Build platforms array based on enabled platforms
const platforms = [];

if ($vars.ENABLE_FACEBOOK === 'true') {
  platforms.push({
    platform: 'facebook',
    content: content.facebook,
    enabled: true
  });
}

if ($vars.ENABLE_INSTAGRAM === 'true') {
  platforms.push({
    platform: 'instagram',
    content: content.instagram,
    enabled: true
  });
}

if ($vars.ENABLE_TIKTOK === 'true') {
  platforms.push({
    platform: 'tiktok',
    content: content.tiktok,
    enabled: true
  });
}

if ($vars.ENABLE_LINKEDIN === 'true') {
  platforms.push({
    platform: 'linkedin',
    content: content.linkedin,
    enabled: true
  });
}

return [{
  json: {
    ...content,
    enabledPlatforms: platforms,
    platformCount: platforms.length
  }
}];

