parameters.jsCode:
const url = $json.data
  ? JSON.parse($json.data.resultJson).resultUrls[0]
  : $json.imageUrl; // dacƒÉ ai deja imageUrl
const fn = url.split('/').pop() || 'image.bin';
const ext = fn.split('.').pop()?.toLowerCase();
const ct = (ext === 'png') ? 'image/png'
        : (ext === 'webp') ? 'image/webp'
        : (ext === 'gif') ? 'image/gif'
        : 'image/jpeg';
return [{ json: { imageUrl: url, filename: fn, contentType: ct } }];

// Collect all Late Create Post results from the Merge
const items = $input.all();
const posts = items.map(i => i.json);

// Helpers
const fmtPlatform = p => p?.platforms?.[0] || 'unknown';
const fmtId = p => p?._id || 'N/A';
const preview = s => (s || '').slice(0, 100) + (s && s.length > 100 ? '‚Ä¶' : '');

// Build per-platform lines
const lines = posts.map(p =>
  `‚Ä¢ ${fmtPlatform(p)} ‚Äî ID: ${fmtId(p)} ‚Äî "${preview(p.content)}"`
);

// Build unified message
const nowLocal = new Date().toLocaleString();
const nowISO = new Date().toISOString();
const notificationMessage =
  `üéâ Social Media Posts Published Successfully!\n\n` +
  `üìã Details (${posts.length}):\n` +
  lines.join('\n') +
  `\n\n‚úÖ Workflow completed at ${nowISO}`;

// Structured payload for downstream (webhook/Slack/Discord/Email)
const webhookData = {
  status: 'SUCCESS',
  count: posts.length,
  posts: posts.map(p => ({
    postId: fmtId(p),
    platform: fmtPlatform(p),
    contentPreview: preview(p.content),
    createdAt: p.createdAt || nowISO,
    userId: p.userId || 'N/A'
  })),
  createdAt: nowISO,
  slackMessage: `‚úÖ Published on: ${posts.map(p => fmtPlatform(p)).join(', ')} | ` +
                `IDs: ${posts.map(p => fmtId(p)).join(', ')}`,
  discordMessage: `üöÄ **Posts Published (${posts.length})**\n` +
                  lines.join('\n') + `\n\n**Time:** ${nowLocal}`,
  emailSubject: `Social Posts Published (${posts.length})`,
  emailBody: notificationMessage,
  webhookNotification: notificationMessage
};

// Return a single consolidated item
return [{
  json: {
    posts,
    notification: webhookData,
    webhookMessage: notificationMessage,
    executionStatus: 'SUCCESS'
  }
}];

// Combine content data with media URLs
const mediaData = $input.first().json;
const contentData = $('Parse & Filter Platforms').item.json;

return [{
  json: {
    ...contentData,  // facebook, instagram, hashtags, etc.
    ...mediaData,    // url, publicUrl, files, etc.
  }
}];
