// Access the articles array from the current node's input
const articles = $input.first().json.articles || [];

// Log the number of articles for debugging
console.log(`Found ${articles.length} articles`);

// Aggregate the articles into an HTML-formatted string
const emailBody = `

  <hr style="border: 1px solid #ddd;">
  ${articles.map((article, index) => `
    <div style="margin-bottom: 20px;">
      <h2 style="color: #1a73e8;">Article ${index + 1}: ${article.title}</h2>
      <p><strong>Description:</strong> ${article.description}</p>
    </div>
    <hr style="border: 1px solid #ddd;">
  `).join('')}
`;

// Output a single item with the HTML email body
const output = [{
  json: {
    email_subject: "Latest News Articles",
    email_body: emailBody
  }
}];

// Log the final output for debugging
console.log('Output item:', JSON.stringify(output, null, 2));

// Return the single item
return output;

