// Helper function to escape special characters for JSON
function escapeJsonString(str) {
  if (typeof str !== 'string') {
    return String(str || 'Default commentary');
  }
  return str
    .replace(/\\/g, '\\\\') // Escape backslashes
    .replace(/"/g, '\\"')   // Escape double quotes
    .replace(/\n/g, '\\n')  // Escape newlines
    .replace(/\t/g, '\\t'); // Escape tabs
}

// Get all input items
const items = $input.all();

// Check if there are any items
if (!items.length) {
  console.log("No input items found");
  return [{ json: { error: "No input items" } }];
}

// Take the first item's json.output.commentary (adjust if you need a different item)
const commentary = items[0].json.output?.commentary;

const linkedInProfileId = 'urn:li:person:default-id';



// Construct the JSON payload
const payload = {
  author: linkedInProfileId,


  commentary: escapeJsonString(commentary),
  visibility: "PUBLIC",
  lifecycleState: "PUBLISHED",
  distribution: {
    feedDistribution: "MAIN_FEED",
    targetEntities: [],
    thirdPartyDistributionChannels: []
  }
};

// Log for debugging
console.log("Dynamic JSON:", JSON.stringify(payload, null, 2));
console.log("Raw Commentary:", commentary);
console.log("Escaped Commentary:", payload.commentary);
console.log("Commentary Type:", typeof commentary);

return [{ json: payload }];
