// Markdown → Google Docs batchUpdate (headings/lists/inline + real tables; fixed delete ranges)
// Input: items[0].json.text (Markdown)
// Output: { json: { requests: [...] } }

const rawInput = items[0].json.text ?? "";

/* =========================
   Helpers & Normalization
   ========================= */
function hexToRgb(hex) {
  if (!hex || !hex.startsWith('#')) return null;
  const r = parseInt(hex.slice(1, 3), 16) / 255;
  const g = parseInt(hex.slice(3, 5), 16) / 255;
  const b = parseInt(hex.slice(5, 7), 16) / 255;
  return { red: r, green: g, blue: b };
}
function headingStyleForLevel(level) {
  const map = { 1: 'HEADING_1', 2: 'HEADING_2', 3: 'HEADING_3', 4: 'HEADING_4', 5: 'HEADING_5', 6: 'HEADING_6' };
  return map[level] || 'HEADING_1';
}
// Collapse 3+ blank lines → 2; trim trailing spaces
function normalizeMarkdown(md) {
  return md
    .replace(/\r\n/g, '\n')
    .replace(/[ \t]+\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .replace(/[ \t]+$/gm, '');
}

// Parse inline markdown for one line → { text, spans[] } (spans: { offsetStart, offsetEnd, bold?, italic?, link? })
function parseInline(line) {
  let text = line;
  const spans = [];

  // Safety: handle legacy LINK markers
  if (text.includes('\u0001LINKSTART\u0001')) {
    let acc = '';
    for (let i = 0; i < text.length; ) {
      if (text.startsWith('\u0001LINKSTART\u0001', i)) {
        i += '\u0001LINKSTART\u0001'.length;
        const urlTag = '\u0001LINKURL:';
        const endTag = '\u0001LINKEND\u0001';
        const urlIdx = text.indexOf(urlTag, i);
        const endIdx = text.indexOf(endTag, i);
        const label = text.slice(i, urlIdx);
        const url = text.slice(urlIdx + urlTag.length, endIdx);
        const start = acc.length;
        acc += label;
        spans.push({ offsetStart: start, offsetEnd: start + label.length, link: url });
        i = endIdx + endTag.length;
      } else acc += text[i++];
    }
    text = acc;
  }

  // Markdown links: [label](url)
  text = text.replace(/\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g, (_m, label, url) => `\u0002L${label}\u0002U${url}\u0002E`);

  // Bold+Italic ***text*** or ___text___
  text = text.replace(/(\*{3}|_{3})([\s\S]*?)\1/g, (_m, _w, inner) => `\u0002BIS${inner}\u0002BIE`);
  // Bold **text** or __text__
  text = text.replace(/(\*{2}|__)([\s\S]*?)\1/g, (_m, _w, inner) => `\u0002BS${inner}\u0002BE`);
  // Italic *text* or _text_
  text = text.replace(/(\*|_)([\s\S]*?)\1/g, (_m, _w, inner) => `\u0002IS${inner}\u0002IE`);

  // Decode markers → clean text + spans
  let clean = '';
  for (let i = 0; i < text.length; ) {
    if (text.startsWith('\u0002L', i)) {
      i += 2;
      const uIdx = text.indexOf('\u0002U', i);
      const eIdx = text.indexOf('\u0002E', i);
      const label = text.slice(i, uIdx);
      const url = text.slice(uIdx + 2, eIdx);
      const start = clean.length;
      clean += label;
      spans.push({ offsetStart: start, offsetEnd: start + label.length, link: url });
      i = eIdx + 2;
      continue;
    }
    if (text.startsWith('\u0002BIS', i)) {
      i += 4; const eIdx = text.indexOf('\u0002BIE', i);
      const inner = text.slice(i, eIdx);
      const start = clean.length; clean += inner;
      spans.push({ offsetStart: start, offsetEnd: start + inner.length, bold: true, italic: true });
      i = eIdx + 4; continue;
    }
    if (text.startsWith('\u0002BS', i)) {
      i += 3; const eIdx = text.indexOf('\u0002BE', i);
      const inner = text.slice(i, eIdx);
      const start = clean.length; clean += inner;
      spans.push({ offsetStart: start, offsetEnd: start + inner.length, bold: true });
      i = eIdx + 3; continue;
    }
    if (text.startsWith('\u0002IS', i)) {
      i += 3; const eIdx = text.indexOf('\u0002IE', i);
      const inner = text.slice(i, eIdx);
      const start = clean.length; clean += inner;
      spans.push({ offsetStart: start, offsetEnd: start + inner.length, italic: true });
      i = eIdx + 3; continue;
    }
    clean += text[i++];
  }
  return { text: clean, spans };
}

/* =========================
   Table parsing utilities
   ========================= */
function isTableSeparator(line) {
  if (!line) return null;
  const cleaned = line.trim();
  const noEdges = cleaned.replace(/^\|/, '').replace(/\|$/, '');
  const parts = noEdges.split('|').map(s => s.trim());
  if (!parts.length) return null;
  const aligns = [];
  for (const p of parts) {
    if (!/^-{3,}:?$|^:?-{3,}:?$/.test(p)) return null;
    const left = p.startsWith(':'), right = p.endsWith(':');
    aligns.push(left && right ? 'CENTER' : (!left && right ? 'END' : 'START'));
  }
  return aligns;
}
function parseTableRow(line) {
  if (!line) return null;
  const t = line.trim();
  if (!t.includes('|')) return null;
  if (/^`{3,}/.test(t) || /^\s*(?:-{3,}|_{3,}|\*{3,})\s*$/.test(t)) return null;
  const noEdges = t.replace(/^\|/, '').replace(/\|$/, '');
  return noEdges.split('|').map(c => c.replace(/\\\|/g, '|').trim());
}

/* =========================
   Block-level parser (with tables)
   ========================= */
function parseMarkdownToModel(md) {
  const lines = normalizeMarkdown(md).split('\n');

  let docText = '';
  const blocks = [];       // { start, end, kind, level? }
  const inlineSpans = [];  // non-table inline spans
  const listGroups = [];   // contiguous list ranges
  const tableBlocks = [];  // tables to materialize
  let cursor = 0;

  function addListLine(kind, start, end) {
    const last = listGroups[listGroups.length - 1];
    if (last && last.kind === kind && last.end === start) last.end = end;
    else listGroups.push({ kind, start, end });
  }

  for (let i = 0; i < lines.length; i++) {
    const raw = lines[i];

    // HR
    if (/^\s*(?:-{3,}|_{3,}|\*{3,})\s*$/.test(raw)) {
      const hr = '────────────────────────\n';
      const start = cursor; docText += hr; const end = cursor + hr.length;
      blocks.push({ start, end, kind: 'hr' });
      cursor = end; continue;
    }

    // Table?
    const header = parseTableRow(raw);
    const aligns = isTableSeparator(lines[i + 1] || '');
    if (header && aligns) {
      const start = cursor;
      docText += raw + '\n'; cursor += (raw + '\n').length;
      const sepLine = lines[i + 1];
      docText += sepLine + '\n'; cursor += (sepLine + '\n').length;

      const colCount = aligns.length;
      const rowsText = [];
      const rowsSpans = [];

      const headerParsed = header.slice(0, colCount);
      while (headerParsed.length < colCount) headerParsed.push('');
      const headerInline = headerParsed.map(c => parseInline(c));
      rowsText.push(headerInline.map(o => o.text));
      rowsSpans.push(headerInline.map(o => o.spans));

      let j = i + 2;
      while (j < lines.length) {
        const r = parseTableRow(lines[j]);
        if (!r) break;
        const normalized = r.slice(0, colCount);
        while (normalized.length < colCount) normalized.push('');
        const parsed = normalized.map(c => parseInline(c));
        rowsText.push(parsed.map(o => o.text));
        rowsSpans.push(parsed.map(o => o.spans));

        docText += lines[j] + '\n';
        cursor += (lines[j] + '\n').length;
        j++;
      }
      const end = cursor;

      tableBlocks.push({ start, end, colCount, aligns, rowsText, rowsSpans });
      blocks.push({ start, end, kind: 'table' });

      i = j - 1;
      continue;
    }

    // Heading
    const h = raw.match(/^(\s{0,3})(#{1,6})\s+(.*)$/);
    if (h) {
      const level = h[2].length;
      const { text, spans } = parseInline(h[3]);
      const para = text + '\n'; const start = cursor;
      docText += para; const end = cursor + para.length;
      blocks.push({ start, end, kind: 'heading', level });
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, ...sp }));
      cursor = end; continue;
    }

    // Blockquote
    const bq = raw.match(/^\s*>\s?(.*)$/);
    if (bq) {
      const { text, spans } = parseInline(bq[1]);
      const para = text + '\n'; const start = cursor;
      docText += para; const end = cursor + para.length;
      blocks.push({ start, end, kind: 'blockquote' });
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, ...sp }));
      cursor = end; continue;
    }

    // Numbered list
    const num = raw.match(/^\s*\d+\.\s+(.*)$/);
    if (num) {
      const { text, spans } = parseInline(num[1]);
      const para = text + '\n'; const start = cursor;
      docText += para; const end = cursor + para.length;
      blocks.push({ start, end, kind: 'numbered' });
      addListLine('numbered', start, end);
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, ...sp }));
      cursor = end; continue;
    }

    // Bulleted list
    const bul = raw.match(/^\s*([-*•])\s+(.*)$/);
    if (bul) {
      const { text, spans } = parseInline(bul[2]);
      const para = text + '\n'; const start = cursor;
      docText += para; const end = cursor + para.length;
      blocks.push({ start, end, kind: 'bulleted' });
      addListLine('bulleted', start, end);
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, ...sp }));
      cursor = end; continue;
    }

    // Paragraph
    {
      const { text, spans } = parseInline(raw);
      const para = text + '\n'; const start = cursor;
      docText += para; const end = cursor + para.length;
      blocks.push({ start, end, kind: 'paragraph' });
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, ...sp }));
      cursor = end;
    }
  }

  return { docText, blocks, inlineSpans, listGroups, tableBlocks };
}

/* =========================
   Build requests (strict-safe ranges)
   ========================= */
const { docText, blocks, inlineSpans, listGroups, tableBlocks } = parseMarkdownToModel(rawInput);

// Insert at 1 to skip the first section break at 0
const baseOffset = 1;
const docLen = docText.length;
const segmentEnd = baseOffset + docLen; // exclusive

// General range: clamp to stay strictly < segment end (for style ops)
function absRangeSafe(relStart, relEnd) {
  const start = Math.max(baseOffset + relStart, baseOffset);
  const desiredEnd = baseOffset + relEnd;
  const hardMax = segmentEnd - 1; // must be < segment end
  const end = Math.max(Math.min(desiredEnd, hardMax), start + 1);
  if (end <= start) return null;
  return { startIndex: start, endIndex: end };
}
// Exact range for DELETE (we want to remove the whole table text; it's always inside body)
function absRangeExactForDelete(relStart, relEnd) {
  const start = baseOffset + relStart;
  let end = baseOffset + relEnd;
  if (end >= segmentEnd) end = segmentEnd - 1; // keep < segment end
  if (end <= start) return null;
  return { startIndex: start, endIndex: end };
}
function absIndex(rel) { return baseOffset + rel; }

const requests = [];

/* 1) Insert full markdown text (including raw table text we’ll convert later) */
requests.push({ insertText: { location: { index: baseOffset }, text: docText } });

/* 2) Headings & blockquotes (no global style pass) */
for (const b of blocks) {
  if (b.kind === 'heading') {
    const rng = absRangeSafe(b.start, b.end);
    if (!rng) continue;
    requests.push({
      updateParagraphStyle: {
        range: rng,
        paragraphStyle: {
          namedStyleType: headingStyleForLevel(b.level),
          alignment: 'START',
          spaceAbove: { magnitude: 0, unit: 'PT' },
          spaceBelow: { magnitude: 0, unit: 'PT' },
          indentStart: { magnitude: 0, unit: 'PT' },
          indentFirstLine: { magnitude: 0, unit: 'PT' }
        },
        fields: 'namedStyleType,alignment,spaceAbove,spaceBelow,indentStart,indentFirstLine'
      }
    });
  } else if (b.kind === 'blockquote') {
    const rng = absRangeSafe(b.start, b.end);
    if (!rng) continue;
    requests.push({
      updateParagraphStyle: {
        range: rng,
        paragraphStyle: {
          namedStyleType: 'NORMAL_TEXT',
          alignment: 'START',
          spaceAbove: { magnitude: 0, unit: 'PT' },
          spaceBelow: { magnitude: 0, unit: 'PT' },
          indentStart: { magnitude: 18, unit: 'PT' },
          indentFirstLine: { magnitude: 18, unit: 'PT' }
        },
        fields: 'namedStyleType,alignment,spaceAbove,spaceBelow,indentStart,indentFirstLine'
      }
    });
    requests.push({ updateTextStyle: { range: rng, textStyle: { italic: true }, fields: 'italic' } });
  }
}

/* 3) Lists → bullets */
for (const g of listGroups) {
  const rng = absRangeSafe(g.start, g.end);
  if (!rng) continue;
  requests.push({
    createParagraphBullets: {
      range: rng,
      bulletPreset: g.kind === 'numbered' ? 'NUMBERED_DECIMAL_ALPHA_ROMAN' : 'BULLET_DISC_CIRCLE_SQUARE'
    }
  });
}

/* 4) Inline styles for non-table text */
for (const sp of inlineSpans) {
  const rng = absRangeSafe(sp.start, sp.end);
  if (!rng) continue;
  const fields = [];
  const textStyle = {};
  if (sp.bold) { textStyle.bold = true; fields.push('bold'); }
  if (sp.italic) { textStyle.italic = true; fields.push('italic'); }
  if (sp.link) { textStyle.link = { url: sp.link }; fields.push('link'); }
  if (!fields.length) continue;
  requests.push({ updateTextStyle: { range: rng, textStyle, fields: fields.join(',') } });
}

/* 5) Convert Markdown tables → real Docs tables (process bottom-up to keep indices stable) */
function buildTableInsertions(tableAbsIndex, rowsText /* 2D array */) {
  // Heuristic indexes that work for newly inserted tables: first cell content is a few chars after table start,
  // and each following cell is offset by ~2. We still reverse insertions so earlier inserts don't shift later targets.
  const cols = Math.max(...rowsText.map(r => r.length));
  let idx = tableAbsIndex + 5;
  const insertions = [];
  const cellStarts = rowsText.map(() => new Array(cols).fill(null));
  rowsText.forEach((row, i) => {
    const rowIndex = idx + (i === 0 ? 0 : 3) - 1;
    row.forEach((cellText, j) => {
      const cellIdx = rowIndex + j * 2;
      cellStarts[i][j] = cellIdx;
      insertions.push({ index: cellIdx, text: cellText, r: i, c: j });
      idx = cellIdx + 1;
    });
    if (row.length < cols) idx += (cols - row.length) * 2;
  });
  insertions.reverse();
  return { insertions, cellStarts, columns: cols };
}
function paragraphAlignmentRequest(start, end, align) {
  return {
    updateParagraphStyle: {
      range: { startIndex: start, endIndex: end },
      paragraphStyle: { alignment: align },
      fields: 'alignment'
    }
  };
}
const headerTextBg = hexToRgb('#F3F4F6');
const tablesDesc = [...tableBlocks].sort((a, b) => b.start - a.start);

for (const tb of tablesDesc) {
  const absStart = absIndex(tb.start);

  // Delete raw Markdown table text EXACTLY (no leftover pipes/spaces)
  const del = absRangeExactForDelete(tb.start, tb.end);
  if (!del) continue;
  requests.push({ deleteContentRange: { range: del } });

  // Insert empty table at same location
  requests.push({ insertTable: { rows: tb.rowsText.length, columns: tb.colCount, location: { index: absStart } } });

  // Insert cell text (reverse order) and capture starts for styling
  const { insertions, cellStarts } = buildTableInsertions(absStart, tb.rowsText);
  for (const ins of insertions) {
    requests.push({ insertText: { location: { index: ins.index }, text: ins.text } });
  }

  // Header: bold + text background (no tableStartLocation usage)
  for (let j = 0; j < tb.colCount; j++) {
    const s = cellStarts[0][j];
    const t = tb.rowsText[0][j] || '';
    if (s != null && t.length) {
      requests.push({
        updateTextStyle: {
          range: { startIndex: s, endIndex: s + t.length },
          textStyle: { bold: true, backgroundColor: { color: { rgbColor: headerTextBg } } },
          fields: 'bold,backgroundColor'
        }
      });
    }
  }

  // Alignment per column on all rows
  for (let r = 0; r < tb.rowsText.length; r++) {
    for (let c = 0; c < tb.colCount; c++) {
      const s = cellStarts[r][c];
      const t = tb.rowsText[r][c] || '';
      if (s == null || !t.length) continue;
      requests.push(paragraphAlignmentRequest(s, s + t.length, tb.aligns[c] || 'START'));
    }
  }

  // Inline styles inside cells
  for (let r = 0; r < tb.rowsText.length; r++) {
    for (let c = 0; c < tb.colCount; c++) {
      const s = cellStarts[r][c];
      const spans = tb.rowsSpans[r][c] || [];
      for (const sp of spans) {
        const start = s + sp.offsetStart;
        const end = s + sp.offsetEnd;
        const fields = [];
        const textStyle = {};
        if (sp.bold) { textStyle.bold = true; fields.push('bold'); }
        if (sp.italic) { textStyle.italic = true; fields.push('italic'); }
        if (sp.link) { textStyle.link = { url: sp.link }; fields.push('link'); }
        if (fields.length) requests.push({ updateTextStyle: { range: { startIndex: start, endIndex: end }, textStyle, fields: fields.join(',') } });
      }
    }
  }
}

return { json: { requests } };
