// Native implementation of Mersenne Twister MT19937
class MersenneTwister {
    constructor(seed) {
        this.MT = new Array(624);
        this.index = 0;
        this.MT[0] = seed || Date.now();
        for (let i = 1; i < 624; i++) {
            this.MT[i] = (1812433253 * (this.MT[i - 1] ^ (this.MT[i - 1] >>> 30)) + i) >>> 0;
        }
    }

    extractNumber() {
        if (this.index === 0) {
            this.generateNumbers();
        }

        let y = this.MT[this.index];
        y ^= y >>> 11;
        y ^= (y << 7) & 0x9d2c5680;
        y ^= (y << 15) & 0xefc60000;
        y ^= y >>> 18;

        this.index = (this.index + 1) % 624;
        return y >>> 0;
    }

    generateNumbers() {
        for (let i = 0; i < 624; i++) {
            const y = (this.MT[i] & 0x80000000) + (this.MT[(i + 1) % 624] & 0x7fffffff);
            this.MT[i] = this.MT[(i + 397) % 624] ^ (y >>> 1);
            if (y % 2 !== 0) {
                this.MT[i] ^= 0x9908b0df;
            }
        }
    }

    random() {
        return this.extractNumber() / 0x100000000;
    }
}

// Uses current timestamp as seed for generator
const timestamp = Date.now();
const generator = new MersenneTwister(timestamp);

// Function for generating random integer numbers
function random_int(min = 0, max = 10000000000) {
    const range = max - min + 1;
    return Math.floor(generator.random() * range) + min;
}

// Process each input item
for (const item of $input.all()) {
    // Generates random number
    const randomNumber = random_int();

    // Generates random number with customized range
    const customMin = item.json.min || 0;
    const customMax = item.json.max || 10000000000;
    const customRandomNumber = random_int(customMin, customMax);

    // Adds generated number
    item.json.randomNumber = randomNumber;
    item.json.customRandomNumber = customRandomNumber;
    item.json.seed = timestamp;
    item.json.generatedAt = new Date().toISOString();
}

return $input.all();
