// --- Slack Event Normalizer ---
// Works with Slack Trigger (test) or Production webhook body

const ev =
  $json.event ??
  $json.body?.event ??   // prod webhook
  $json;                 // fallback (test)

if (!ev) {
  console.log('No Slack event in payload:', $json);
  return [];
}

// Ignore bot/system messages and edits
if (ev.bot_id || ev.subtype === 'bot_message' || ev.subtype === 'message_changed') {
  return [];
}

// Strip a leading @mention like "<@U123...> "
let text = (ev.text || '').replace(/^<@[^>]+>\s*/g, '').trim();
const lower = text.toLowerCase();

let cmd = null, pick = null, notes = null;

// start/stop/done/revise
if (lower === 'start') {
  cmd = 'start';
} else if (lower === 'stop') {
  cmd = 'stop';
} else if (lower === 'done') {
  cmd = 'done';
} else if (/^revise\[(.*)\]$/i.test(text)) {
  cmd = 'revise';
  notes = text.match(/^revise\[(.*)\]$/i)[1].trim();
}

// gen (supports "2" or "gen 2")
const m = lower.match(/^gen\s*(\d+)$/i);
if (!cmd && m) {
  cmd = 'gen';
  pick = parseInt(m[1], 10);
} else if (!cmd && /^\d+$/.test(lower)) {
  cmd = 'gen';
  pick = parseInt(lower, 10);
}

return [{
  json: {
    cmd,
    pick,
    notes,
    channel: ev.channel || $json.channel,
    ts: ev.ts || $json.ts,
    thread_ts: ev.thread_ts || ev.ts || $json.thread_ts || $json.ts,
    user: ev.user || $json.user,
    text,
  }
}];

/**
 * Code1 ‚Äî after Merge (Append)
 * Build clean, deduped article list and a compact text block for Gemini.
 */

// ====== configuration ======
const MAX_HEADLINES = 60;   // <= how many headlines to pass to Gemini (try 50‚Äì80)

// ====== helpers ======
function stripHtml(html = '') {
  // Remove tags
  const noTags = String(html).replace(/<[^>]*>/g, ' ');
  // Collapse whitespace
  return noTags.replace(/\s+/g, ' ').trim();
}

function truncate(str = '', n = 140) {
  const s = String(str).trim();
  return s.length > n ? s.slice(0, n) + '‚Ä¶' : s;
}

function pickFirst(obj, keys) {
  for (const k of keys) {
    if (obj?.[k]) return obj[k];
  }
  return undefined;
}

// ====== collect & normalize ======
const items = $input.all();              // all merged items
const seen = new Set();                  // dedupe by *normalized title*
const articles = [];

for (const item of items) {
  const j = item.json || {};

  // Try common RSS/Atom/HTTP node fields
  const rawTitle = pickFirst(j, [
    'title', 'headline', 'name'
  ]);

  // If no title, skip (we need a key to dedupe and display)
  if (!rawTitle) continue;

  // For descriptions, try several fields and strip HTML
  const rawDesc = pickFirst(j, [
    'contentSnippet', 'description', 'summary', 'content', 'content:encoded'
  ]);
  const description = rawDesc ? stripHtml(rawDesc) : '';

  // Links/guid
  const link = pickFirst(j, [
    'link', 'url', 'guid'
  ]);

  // Deduplicate by normalized title
  const title = String(rawTitle).trim();
  const key = title.toLowerCase();
  if (seen.has(key)) continue;
  seen.add(key);

  articles.push({
    title,
    description,
    link: link ? String(link).trim() : ''
  });
}

// ====== choose how much to send to Gemini ======
const limited = articles.slice(0, MAX_HEADLINES);

// Build compact arrays for Gemini + debugging
const headlines = limited.map(a => a.title);

// If you want titles + a short blurb in the prompt, use this:
const articleText = limited
  .map((a, i) => `${i + 1}. ${a.title}${a.description ? ` ‚Äî ${truncate(a.description)}` : ''}`)
  .join('\n');

// ====== output ======
return [{
  json: {
    articles: limited,        // array of normalized items
    headlines,                // array of titles only
    articleText,              // numbered list for Gemini
    articleCount: limited.length,
  }
}];

