// Code2 ‚Äî normalize Gemini topics JSON for Slack

const raw =
  $json.text ??
  $json.content?.parts?.[0]?.text ??
  '';

let out = { topics: [] };

try {
  const cleaned = String(raw).replace(/```json|```/gi, '').trim();

  // try full first, then last {...}
  try {
    out = JSON.parse(cleaned);
  } catch {
    const m = cleaned.match(/\{[\s\S]*\}$/);
    if (m) out = JSON.parse(m[0]);
  }
} catch (e) {
  console.log('Failed to parse Gemini output:', e, String(raw).slice(0, 300));
  out = { topics: [] };
}

// --- normalize to [{topic: "..."}]
let topics = out?.topics ?? [];
if (!Array.isArray(topics)) topics = [];

topics = topics
  .map(t => {
    if (typeof t === 'string') return { topic: t.trim() };
    if (t && typeof t === 'object') {
      return { topic: String(t.topic ?? t.headline ?? t.title ?? '').trim() };
    }
    return { topic: '' };
  })
  .filter(t => t.topic);

// guardrail: de-dup & cap (optional)
const seen = new Set();
topics = topics.filter(t => {
  const key = t.topic.toLowerCase();
  if (seen.has(key)) return false;
  seen.add(key);
  return true;
});

// output to next nodes
return [{
  json: {
    mode: 'topics',
    topics
  }
}];

