// post gen ‚Äî map user number to the chosen headline from the thread

// 1) get the user's pick from anywhere we can
function num(x) {
  const n = Number(x);
  return Number.isFinite(n) ? n : undefined;
}

let pick =
  num($json.pick) ??
  num($node["Code: parse_slack_command"].json?.pick) ??                         // from your normalizer Code
  num(($node["Code: parse_slack_command"].json?.text || "").match(/\d+/)?.[0]); // last resort

if (!pick) {
  return [{ json: { error: 'no_pick', msg: "Reply with a number like 2 or 'gen 2'." } }];
}

// 2) normalize Slack replies shape to an array of message objects
//    - Return All OFF: slack node gives { messages: [...] }
//    - Return All ON:  slack node gives many items, one per message
let msgs = [];
if (Array.isArray($json.messages)) {
  msgs = $json.messages;
} else if (Array.isArray($items().map(i => i.json))) {
  // Return All ON case: collect all incoming items as messages
  msgs = $items().map(i => i.json);
}

// 3) find the bot message that listed the topics
//    Adapt the marker below to the exact first line you print in Slack
const LIST_MARKER = 'Trending hotel topics';
let listMsg = null;

// try newest-first if provided by Slack, else do a defensive search
for (const m of msgs) {
  if (typeof m.text === 'string' && m.text.includes(LIST_MARKER)) {
    listMsg = m;
    break;
  }
}
if (!listMsg) {
  for (let i = msgs.length - 1; i >= 0; i--) {
    const m = msgs[i];
    if (typeof m.text === 'string' && m.text.includes(LIST_MARKER)) {
      listMsg = m;
      break;
    }
  }
}
if (!listMsg) {
  return [{ json: { error: 'no_list', msg: 'Could not find the topics list message in this thread.' } }];
}

// 4) parse numbered lines from the list message
//    We accept both "1) Title (12)" and "1) Title" styles
const lines = (listMsg.text || '').split('\n');
const topics = [];
for (const line of lines) {
  // capture: number ) <headline> (optional (count))
  const m = line.match(/^\s*\d+\)\s+(.+?)(?:\s+\(\d+\))?\s*$/);
  if (m) topics.push(m[1].trim());
}

if (!topics.length) {
  return [{ json: { error: 'no_topics', msg: 'Could not parse any topics from the list message.' } }];
}

// clamp pick to available range
const idx = Math.max(1, Math.min(pick, topics.length)) - 1;
const topic = topics[idx];

// 5) pass forward for Gemini post generation
return [{
  json: {
    topic,
    pick: idx + 1,
    topicsCount: topics.length,
    channel: $node["Code: parse_slack_command"].json?.channel,
    thread_ts: $node["Code: parse_slack_command"].json?.thread_ts || $node["Code: parse_slack_command"].json?.ts
  }
}];
