// Process blockchain transaction data
const processTransactionData = (items) => {
  const processedData = items.map(item => {
    const data = item.json;

    // Extract key transaction details
    const transactionData = {
      transactionHash: data.transactionHash || data.signature,
      blockNumber: data.blockNumber || data.slot,
      from: data.from || data.feePayer,
      to: data.to || data.accounts?.[0],
      value: data.value || data.amount,
      gasUsed: data.gasUsed || data.computeUnitsConsumed,
      timestamp: new Date().toISOString(),
      blockchain: data.blockchain || (data.signature ? 'solana' : 'ethereum'),
      contractAddress: data.logs?.[0]?.address || data.programId,
      eventData: data.logs || data.instructions,
      auditTrail: {
        verified: true,
        riskScore: calculateRiskScore(data),
        complianceStatus: checkCompliance(data)
      }
    };

    return { json: transactionData };
  });

  return processedData;
};

// Risk scoring algorithm
const calculateRiskScore = (data) => {
  let score = 0;

  // High value transactions increase risk
  if (parseFloat(data.value || 0) > 1000000) score += 30;

  // Unknown addresses increase risk
  if (!data.from || !data.to) score += 20;

  // Failed transactions increase risk
  if (data.status === 'failed') score += 40;

  // Multiple contract interactions increase risk
  if ((data.logs?.length || 0) > 5) score += 15;

  return Math.min(score, 100);
};

// Compliance check
const checkCompliance = (data) => {
  const checks = {
    kycVerified: true, // Placeholder - integrate with KYC service
    sanctionsCheck: true, // Placeholder - integrate with sanctions list
    jurisdictionCompliant: true,
    reportingRequired: parseFloat(data.value || 0) > 10000
  };

  return checks;
};

return processTransactionData($input.all());
