// Entrada: $json.output con claves es,de,en,fr,it,pt
const langs = ['es','de','en','fr','it','pt'];

const productId = $('Get many products').first().json.id;
const handleES = $('Get many products').first().json.handle
const items = [];
$input.first().json.output.es

for (const lang of langs) {
const node = $input.first().json.output[lang];
if (!node) continue;
items.push({
json: {
product_id: Number(productId),
lang,
handle: node.handle,
shopify_product_name: node.shopify_product_name,
shopify_description: node.shopify_description,
// Para comodidad, también retornamos los campos ES por si quieres compararlos
base_handle_es: handleES
}
});
}


return items;
