// n8n Code node (JavaScript)
// Input: Google Sheets rows (each row as an item) OR one item whose .json is an array of rows.
// Output: ONE item with root-level fields ready for PDF.co Mustache template.

const INVOICE_PREFIX = 'INV-2025-';
const TAX_RATE = 0.00;          // set >0 if you want tax (e.g., 0.07 for 7%)
const DISCOUNT = 0.00;          // flat discount amount
const TERMS = 'Net 14. Thank you for your business!';
const NOTES = 'Please reference the invoice number on payment.';

// ---- Helpers
const fmtUSD = (n) => {
  const v = Number(n || 0);
  return v.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
};
const toNum = (v) => {
  if (v === null || v === undefined || v === '') return 0;
  const n = Number(String(v).replace(/[^0-9.\-]/g, ''));
  return Number.isFinite(n) ? n : 0;
};

// ---- Get rows from input (supports both shapes)
const items = $input.all();
let rows = [];
if (items.length === 1 && Array.isArray(items[0].json)) {
  rows = items[0].json;
} else {
  rows = items.map(i => i.json);
}

// ---- Normalize & filter valid rows
const clean = rows
  .map((r) => ({
    Description: r.Description ?? r.description ?? '',
    Qty: toNum(r.Qty ?? r.qty),
    Price: toNum(r.Price ?? r.price),
    Total: toNum(r.Total ?? r.total), // will recompute if missing
    Company: r.Company ?? r.company ?? '',
    Address: r.Address ?? r.address ?? '',
    Email: r.Email ?? r.email ?? ''
  }))
  .filter(r => r.Description && (r.Qty > 0) && (r.Price > 0 || r.Total > 0));

// ---- Bill-to (use first non-empty)
const first = clean.find(r => r.Company || r.Address || r.Email) || {};
const billTo = {
  name: first.Company || 'Customer',
  address: first.Address || '',
  email: first.Email || ''
};

// ---- Build line items & totals
let subTotal = 0;
const itemsOut = clean.map((r, idx) => {
  const lineTotal = r.Total > 0 ? r.Total : (r.Qty * r.Price);
  subTotal += lineTotal;
  return {
    line: idx + 1,
    description: r.Description,
    qty: r.Qty,
    unitPriceFmt: fmtUSD(r.Price),
    lineTotalFmt: fmtUSD(lineTotal)
  };
});

const taxAmount = subTotal * TAX_RATE;
const grandTotal = subTotal + taxAmount - DISCOUNT;

// ---- Dates & invoice number
const today = new Date();
const invoiceDate = today.toISOString().slice(0, 10);
const due = new Date(today.getTime() + 14 * 24 * 60 * 60 * 1000).toISOString().slice(0, 10);
const invoiceNumber = INVOICE_PREFIX + String(Math.floor(10000 + Math.random() * 90000));

// ---- Final payload (root-level keys for your Mustache template)
const out = {
  invoiceNumber,
  invoiceDate,
  dueDate: due,
  company: {
    name: 'Ynteractive, LLC',
    address: '123 Main Street, Palmyra, PA 17078',
    phone: '111-111-1111',
    email: 'rbreen@ynteractive.com',
    logoUrl: 'https://via.placeholder.com/160x50?text=Ynteractive'
  },
  billTo,
  items: itemsOut,
  subTotalFmt: fmtUSD(subTotal),
  taxRatePct: (TAX_RATE * 100).toFixed(2) + '%',
  taxAmountFmt: fmtUSD(taxAmount),
  discountFmt: fmtUSD(DISCOUNT),
  totalFmt: fmtUSD(grandTotal),
  notes: NOTES,
  terms: TERMS
};

return [out];
