// Dynamic brand profile generator with proper binary data handling
const formData = $input.first().json;
const binaryData = $input.first().binary;

// Extract product information
const productName = formData["What is the product's name?"] || '';
const productTagline = formData["What is the Product Tagline?"] || '';
const productDescription = formData["Product Description"] || '';
const productCategory = formData["What is the Product Category?"] || 'Other';
const highlightedBenefit = formData["Highlighted Benefit"] || '';

// Find the uploaded image binary data
let imageBinary = null;
if (binaryData) {
  // Try different possible field names for the uploaded image
  imageBinary = binaryData['Product Image'] || 
                binaryData['product_image'] || 
                binaryData['data'] ||
                binaryData['Product_Image'] ||
                binaryData[Object.keys(binaryData)[0]]; // Use first available binary
}

// Validate we have image data
if (!imageBinary) {
  throw new Error('No image file found. Please ensure you upload a product image.');
}

// Convert to base64 for AI processing later
let imageBase64 = '';
if (Buffer.isBuffer(imageBinary)) {
  imageBase64 = imageBinary.toString('base64');
} else if (imageBinary.data && Buffer.isBuffer(imageBinary.data)) {
  imageBase64 = imageBinary.data.toString('base64');
}

// Create dynamic brand profile
const dynamicBrandProfile = {
  // AI will populate this based on product analysis
  brandName: productName || "DYNAMIC BRAND",
  brandTone: "Will be determined by AI based on product analysis",
  colorTheme: "AI will extract from product image and market analysis",
  backgroundStyle: "AI will determine optimal style for product category",
  lightingStyle: "AI will optimize for product type and target audience",
  productPlacement: "AI will create placement rules based on product characteristics",
  typographyStyle: "AI will match typography to brand personality",
  compositionGuidelines: "AI will generate composition rules for brand consistency",

  // Metadata for AI processing
  productInfo: {
    name: productName,
    tagline: productTagline,
    description: productDescription,
    category: productCategory,
    benefit: highlightedBenefit,
    imageBase64: imageBase64
  },

  // Processing flags
  needsAIProcessing: true,
  timestamp: new Date().toISOString()
};

// CRITICAL: Return both JSON and binary data
return [{
  json: dynamicBrandProfile,
  binary: {
    data: imageBinary  // Google Drive expects 'data' key
  }
}];

// Process AI Agent output with robust error handling
const input = $input.first();

// Debug: Check what we're getting
console.log('Input structure:', input ? Object.keys(input) : 'input is null');
console.log('Input JSON exists:', !!input?.json);

// Safely get form data
let formData = {};
let originalBinary = {};

try {
  formData = $('On form submission').first()?.json || {};
  originalBinary = $('On form submission').first()?.binary || {};
  console.log('Form data keys:', Object.keys(formData));
  console.log('Binary data keys:', Object.keys(originalBinary));
} catch (e) {
  console.log('Error accessing form data:', e.message);
}

// Safely extract AI Agent output
let aiOutput = {};
try {
  if (input?.json?.output?.output) {
    aiOutput = input.json.output.output;
  } else if (input?.json?.output) {
    aiOutput = input.json.output;
  } else if (input?.json) {
    aiOutput = input.json;
  } else {
    throw new Error('No AI output found');
  }
  console.log('AI Output keys:', Object.keys(aiOutput));
} catch (e) {
  console.log('Error accessing AI output:', e.message);
  console.log('Full input:', JSON.stringify(input, null, 2));
  throw new Error('Cannot access AI Agent output: ' + e.message);
}

// Safely extract assets and other data
const assets = aiOutput.assets || [];
const brandStrategy = aiOutput.brandStrategy || {};
const campaignStrategy = aiOutput.campaignStrategy || {};

console.log('Assets found:', assets.length);

if (assets.length === 0) {
  console.log('No assets in AI output. Full aiOutput:', JSON.stringify(aiOutput, null, 2));
  throw new Error('No assets generated by AI Agent');
}

// Safely prepare product info
const productInfo = {
  name: formData["What is the product's name?"] || 'Unknown Product',
  tagline: formData["What is the Product Tagline?"] || '',
  description: formData["Product Description"] || '',
  category: formData["What is the Product Category?"] || 'General',
  benefit: formData["Highlighted Benefit"] || ''
};

// Safely get binary data
let productImageBinary = null;
try {
  if (originalBinary && Object.keys(originalBinary).length > 0) {
    productImageBinary = originalBinary['Product Image'] || 
                        originalBinary['data'] || 
                        originalBinary[Object.keys(originalBinary)[0]];
  }
} catch (e) {
  console.log('Error accessing binary data:', e.message);
}

if (!productImageBinary) {
  console.log('Available binary keys:', Object.keys(originalBinary || {}));
  throw new Error('Product image binary data not found');
}

// Create outputs with safe property access
const processedAssets = assets.map((asset, index) => {
  const safeAsset = asset || {};
  return {
    json: {
      assetIndex: index,
      assetType: safeAsset.assetType || `Asset ${index + 1}`,
      purpose: safeAsset.purpose || 'Marketing',
      backgroundTone: safeAsset.backgroundTone || 'neutral background',
      surfaceType: safeAsset.surfaceType || 'clean surface',
      accentProp: safeAsset.accentProp || 'minimal styling',
      lighting: safeAsset.lighting || 'natural lighting',
      cameraAngle: safeAsset.cameraAngle || 'straight-on angle',
      overlayText: safeAsset.overlayText || 'Premium Quality',
      priority: safeAsset.priority || 'medium',
      product: productInfo,
      brand: brandStrategy,
      campaignTheme: campaignStrategy.campaignTheme || 'Product Launch',
      timestamp: new Date().toISOString()
    },
    binary: {
      image: productImageBinary
    }
  };
});

console.log('Successfully processed assets:', processedAssets.length);

return processedAssets;
