// Markdown → Google Docs batchUpdate (safe ranges, compact spacing, proper headings/lists/inline)
// Input: items[0].json.output (Markdown)
// Output: { json: { requests: [...] } }

const rawInput = items[0].json.output ?? "";

/* =========================
   Helpers & Normalization
   ========================= */
function hexToRgb(hex) {
  if (!hex || !hex.startsWith('#')) return null;
  const r = parseInt(hex.slice(1, 3), 16) / 255;
  const g = parseInt(hex.slice(3, 5), 16) / 255;
  const b = parseInt(hex.slice(5, 7), 16) / 255;
  return { red: r, green: g, blue: b };
}

function headingStyleForLevel(level) {
  const map = { 1: 'HEADING_1', 2: 'HEADING_2', 3: 'HEADING_3', 4: 'HEADING_4', 5: 'HEADING_5', 6: 'HEADING_6' };
  return map[level] || 'HEADING_1';
}

// Collapse 3+ blank lines → 2; trim trailing spaces
function normalizeMarkdown(md) {
  return md
    .replace(/\r\n/g, '\n')
    .replace(/[ \t]+\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .replace(/[ \t]+$/gm, '');
}

// Parse inline markdown for one line
// Returns { text, spans[] } where spans: { offsetStart, offsetEnd, bold?, italic?, link? }
function parseInline(line) {
  let text = line;
  const spans = [];

  // Handle your previous LINK markers if present (safety)
  // \u0001LINKSTART\u0001Label\u0001LINKURL:https://...\u0001LINKEND\u0001
  if (text.includes('\u0001LINKSTART\u0001')) {
    let acc = '';
    for (let i = 0; i < text.length; ) {
      if (text.startsWith('\u0001LINKSTART\u0001', i)) {
        i += '\u0001LINKSTART\u0001'.length;
        const urlTag = '\u0001LINKURL:';
        const endTag = '\u0001LINKEND\u0001';
        const urlIdx = text.indexOf(urlTag, i);
        const endIdx = text.indexOf(endTag, i);
        const label = text.slice(i, urlIdx);
        const url = text.slice(urlIdx + urlTag.length, endIdx);
        const start = acc.length;
        acc += label;
        spans.push({ offsetStart: start, offsetEnd: start + label.length, link: url });
        i = endIdx + endTag.length;
      } else {
        acc += text[i++];
      }
    }
    text = acc;
  }

  // Markdown links: [label](url)
  text = text.replace(/\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g, (m, label, url) => {
    const start = m.indexOf(label);
    return `\u0002L${label}\u0002U${url}\u0002E`;
  });

  // Bold+Italic ***text*** or ___text___
  text = text.replace(/(\*{3}|_{3})([\s\S]*?)\1/g, (m, _w, inner) => `\u0002BIS${inner}\u0002BIE`);
  // Bold **text** or __text__
  text = text.replace(/(\*{2}|__)([\s\S]*?)\1/g, (m, _w, inner) => `\u0002BS${inner}\u0002BE`);
  // Italic *text* or _text_
  text = text.replace(/(\*|_)([\s\S]*?)\1/g, (m, _w, inner) => `\u0002IS${inner}\u0002IE`);

  // Second pass: build clean text + spans
  let clean = '';
  for (let i = 0; i < text.length; ) {
    if (text.startsWith('\u0002L', i)) {
      i += 2; // \u0002 + 'L'
      const uIdx = text.indexOf('\u0002U', i);
      const eIdx = text.indexOf('\u0002E', i);
      const label = text.slice(i, uIdx);
      const url = text.slice(uIdx + 2, eIdx);
      const start = clean.length;
      clean += label;
      spans.push({ offsetStart: start, offsetEnd: start + label.length, link: url });
      i = eIdx + 2;
      continue;
    }
    if (text.startsWith('\u0002BIS', i)) {
      i += 4;
      const eIdx = text.indexOf('\u0002BIE', i);
      const inner = text.slice(i, eIdx);
      const start = clean.length;
      clean += inner;
      spans.push({ offsetStart: start, offsetEnd: start + inner.length, bold: true, italic: true });
      i = eIdx + 4;
      continue;
    }
    if (text.startsWith('\u0002BS', i)) {
      i += 3;
      const eIdx = text.indexOf('\u0002BE', i);
      const inner = text.slice(i, eIdx);
      const start = clean.length;
      clean += inner;
      spans.push({ offsetStart: start, offsetEnd: start + inner.length, bold: true });
      i = eIdx + 3;
      continue;
    }
    if (text.startsWith('\u0002IS', i)) {
      i += 3;
      const eIdx = text.indexOf('\u0002IE', i);
      const inner = text.slice(i, eIdx);
      const start = clean.length;
      clean += inner;
      spans.push({ offsetStart: start, offsetEnd: start + inner.length, italic: true });
      i = eIdx + 3;
      continue;
    }
    clean += text[i++];
  }
  return { text: clean, spans };
}

/* =========================
   Block-level parser
   ========================= */
function parseMarkdownToModel(md) {
  const lines = normalizeMarkdown(md).split('\n');

  let docText = '';
  const blocks = [];       // { start, end, kind: 'heading'|'paragraph'|'blockquote'|'hr'|'numbered'|'bulleted', level? }
  const inlineSpans = [];  // { start, end, bold?, italic?, link? }
  const listGroups = [];   // contiguous list ranges → { kind, start, end }
  let cursor = 0;

  function addListLine(kind, start, end) {
    const last = listGroups[listGroups.length - 1];
    if (last && last.kind === kind && last.end === start) last.end = end;
    else listGroups.push({ kind, start, end });
  }

  for (let i = 0; i < lines.length; i++) {
    const raw = lines[i];

    // Horizontal rule
    if (/^\s*(?:-{3,}|_{3,}|\*{3,})\s*$/.test(raw)) {
      const hr = '────────────────────────\n';
      const start = cursor;
      docText += hr;
      const end = cursor + hr.length;
      blocks.push({ start, end, kind: 'hr' });
      cursor = end;
      continue;
    }

    // Headings
    const h = raw.match(/^(\s{0,3})(#{1,6})\s+(.*)$/);
    if (h) {
      const level = h[2].length;
      const { text, spans } = parseInline(h[3]);
      const para = text + '\n';
      const start = cursor;
      docText += para;
      const end = cursor + para.length;
      blocks.push({ start, end, kind: 'heading', level });
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, bold: sp.bold, italic: sp.italic, link: sp.link }));
      cursor = end;
      continue;
    }

    // Blockquote
    const bq = raw.match(/^\s*>\s?(.*)$/);
    if (bq) {
      const { text, spans } = parseInline(bq[1]);
      const para = text + '\n';
      const start = cursor;
      docText += para;
      const end = cursor + para.length;
      blocks.push({ start, end, kind: 'blockquote' });
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, bold: sp.bold, italic: sp.italic, link: sp.link }));
      cursor = end;
      continue;
    }

    // Numbered list
    const num = raw.match(/^\s*\d+\.\s+(.*)$/);
    if (num) {
      const { text, spans } = parseInline(num[1]);
      const para = text + '\n';
      const start = cursor;
      docText += para;
      const end = cursor + para.length;
      blocks.push({ start, end, kind: 'numbered' });
      addListLine('numbered', start, end);
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, bold: sp.bold, italic: sp.italic, link: sp.link }));
      cursor = end;
      continue;
    }

    // Bulleted list
    const bul = raw.match(/^\s*([-*•])\s+(.*)$/);
    if (bul) {
      const { text, spans } = parseInline(bul[2]);
      const para = text + '\n';
      const start = cursor;
      docText += para;
      const end = cursor + para.length;
      blocks.push({ start, end, kind: 'bulleted' });
      addListLine('bulleted', start, end);
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, bold: sp.bold, italic: sp.italic, link: sp.link }));
      cursor = end;
      continue;
    }

    // Paragraph
    {
      const { text, spans } = parseInline(raw);
      const para = text + '\n';
      const start = cursor;
      docText += para;
      const end = cursor + para.length;
      blocks.push({ start, end, kind: 'paragraph' });
      spans.forEach(sp => inlineSpans.push({ start: start + sp.offsetStart, end: start + sp.offsetEnd, bold: sp.bold, italic: sp.italic, link: sp.link }));
      cursor = end;
    }
  }

  return { docText, blocks, inlineSpans, listGroups };
}

/* =========================
   Build requests (safe ranges)
   ========================= */
const { docText, blocks, inlineSpans, listGroups } = parseMarkdownToModel(rawInput);

// Insert at 1 to skip the first section break at 0
const baseOffset = 1;
const docLen = docText.length;
const bodyEndExclusive = baseOffset + docLen;

// Safe range builder: clamp end < bodyEndExclusive (end is exclusive per API)
function absRangeSafe(relStart, relEnd) {
  const start = Math.max(baseOffset + relStart, baseOffset);
  const maxEnd = Math.max(Math.min(baseOffset + relEnd, bodyEndExclusive - 1), start); // clamp exclusive
  if (maxEnd <= start) return null; // skip zero/invalid
  return { startIndex: start, endIndex: maxEnd };
}

const requests = [];

// 1) Insert full text
requests.push({ insertText: { location: { index: baseOffset }, text: docText } });

// 2) Default text styling across the inserted range (clamped end)
const rAll = absRangeSafe(0, docLen);
if (rAll) {
  requests.push({
    updateTextStyle: {
      range: rAll,
      textStyle: {
        weightedFontFamily: { fontFamily: 'Sofia Sans' },
        fontSize: { magnitude: 12.5, unit: 'PT' },
        foregroundColor: { color: { rgbColor: hexToRgb('#0d1216') } }
      },
      fields: 'weightedFontFamily,fontSize,foregroundColor'
    }
  });
}

// 3) Headings & blockquotes (tight spacing to avoid big gaps)
for (const b of blocks) {
  if (b.kind === 'heading') {
    const rng = absRangeSafe(b.start, b.end);
    if (!rng) continue;
    requests.push({
      updateParagraphStyle: {
        range: rng,
        paragraphStyle: {
          namedStyleType: headingStyleForLevel(b.level),
          alignment: 'START',
          spaceAbove: { magnitude: 0, unit: 'PT' },
          spaceBelow: { magnitude: 0, unit: 'PT' },
          indentStart: { magnitude: 0, unit: 'PT' },
          indentFirstLine: { magnitude: 0, unit: 'PT' }
        },
        fields: 'namedStyleType,alignment,spaceAbove,spaceBelow,indentStart,indentFirstLine'
      }
    });
  } else if (b.kind === 'blockquote') {
    const rng = absRangeSafe(b.start, b.end);
    if (!rng) continue;
    requests.push({
      updateParagraphStyle: {
        range: rng,
        paragraphStyle: {
          namedStyleType: 'NORMAL_TEXT',
          alignment: 'START',
          spaceAbove: { magnitude: 0, unit: 'PT' },
          spaceBelow: { magnitude: 0, unit: 'PT' },
          indentStart: { magnitude: 18, unit: 'PT' },
          indentFirstLine: { magnitude: 18, unit: 'PT' }
        },
        fields: 'namedStyleType,alignment,spaceAbove,spaceBelow,indentStart,indentFirstLine'
      }
    });
    requests.push({ updateTextStyle: { range: rng, textStyle: { italic: true }, fields: 'italic' } });
  }
}

// 4) Lists (convert contiguous ranges to bullets; clamp ranges)
for (const g of listGroups) {
  const rng = absRangeSafe(g.start, g.end);
  if (!rng) continue;
  requests.push({
    createParagraphBullets: {
      range: rng,
      bulletPreset: g.kind === 'numbered' ? 'NUMBERED_DECIMAL_ALPHA_ROMAN' : 'BULLET_DISC_CIRCLE_SQUARE'
    }
  });
}

// 5) Inline styles (bold/italic/links)
for (const sp of inlineSpans) {
  const rng = absRangeSafe(sp.start, sp.end);
  if (!rng) continue;
  const fields = [];
  const textStyle = {};
  if (sp.bold) { textStyle.bold = true; fields.push('bold'); }
  if (sp.italic) { textStyle.italic = true; fields.push('italic'); }
  if (sp.link) { textStyle.link = { url: sp.link }; fields.push('link'); }
  if (!fields.length) continue;
  requests.push({ updateTextStyle: { range: rng, textStyle, fields: fields.join(',') } });
}

return { json: { requests } };
