// Function to extract and clean content from specified tags
function extractAndCleanContent(html, tag) {
    const regex = new RegExp(`<${tag}[^>]*>(.*?)<\/${tag}>`, "gis");
    const matches = [...html.matchAll(regex)];

    // Remove nested HTML tags and return plain text
    return matches.map(match => match[1].replace(/<[^>]+>/g, "").trim());
}

// Extract and clean body text (from <p> and <span> tags)
const bodyText = [
    ...extractAndCleanContent(html, "p"),
    ...extractAndCleanContent(html, "span")
];

// Return the clean text as an array
return {
  bodyText
};
