// Fonction pour extraire et regrouper les titres par niveau
function extractAndGroupTitles(markdownText) {
  // Expression régulière pour capturer les titres h1-h6
  const headingRegex = /^(#{1,6})\s+(.+)$/gm;

  // Initialiser tous les niveaux avec "pas de h{n}"
  const titlesByLevel = {
    h1: [],
    h2: [],
    h3: [],
    h4: [],
    h5: [],
    h6: []
  };

  let match;

  // Parcourir toutes les correspondances
  while ((match = headingRegex.exec(markdownText)) !== null) {
    const level = match[1].length; // Nombre de #
    let text = match[2].trim();

    // Retirer les liens markdown : [texte](url) -> texte
    text = text.replace(/\[([^\]]+)\]\([^)]+\)/g, '$1');

    const levelTag = `h${level}`;

    titlesByLevel[levelTag].push(text);
  }

  // Créer l'objet de résultat
  const result = {};

  Object.keys(titlesByLevel).forEach(level => {
    if (titlesByLevel[level].length === 0) {
      result[level] = `${level} : pas de ${level}`;
    } else {
      result[level] = `${level} :\n${titlesByLevel[level].join('\n')}`;
    }
  });

  return result;
}

