const raw= $('Analyze image').first().json.candidates[0].content.parts[0].text;

// remove code fences if present
const cleaned = raw.replace(/```json|```/g, "").trim();

// parse JSON
const parsed = JSON.parse(cleaned);

// wrap in single key
return {
  output: parsed
};

