/**
 * Fonction pour traiter les URLs collect√©es par Firecrawl et g√©n√©rer une arborescence de site
 * en traitant s√©par√©ment les diff√©rents domaines et sous-domaines
 * 
 * @param {Object} inputData - Les donn√©es brutes de l'appel Firecrawl
 * @returns {Array} - Tableau d'objets avec les colonnes pour Google Sheets
 */
function createSiteHierarchy(inputData) {
  // V√©rifier que les donn√©es d'entr√©e sont valides
  if (!inputData || !inputData.success || !Array.isArray(inputData.links) || inputData.links.length === 0) {
    throw new Error("Donn√©es d'entr√©e invalides ou vides");
  }

  // Normaliser toutes les URLs (convertir http en https)
  const urls = inputData.links.map(url => {
    if (url.startsWith('http://')) {
      return 'https://' + url.substring(7);
    }
    return url;
  });

  // Extraire les diff√©rents domaines/sous-domaines pr√©sents dans les URLs
  const domainPattern = /^https?:\/\/([^\/]+)/;
  const domains = {};

  // Regrouper les URLs par domaine/sous-domaine
  for (const url of urls) {
    const match = url.match(domainPattern);
    if (!match) continue;

    const fullDomain = match[1]; // ex: www.zest.fr, wiki.zest.fr

    // Extraire le sous-domaine et le domaine de base
    const domainParts = fullDomain.split('.');
    const isSubdomain = domainParts.length > 2;

    // D√©terminer le domaine principal
    let mainDomain;
    if (isSubdomain) {
      // Pour les sous-domaines comme wiki.zest.fr
      mainDomain = domainParts.slice(domainParts.length - 2).join('.');
    } else {
      // Pour les domaines principaux comme zest.fr
      mainDomain = fullDomain;
    }

    // Enregistrer cette URL dans son groupe de domaine
    if (!domains[fullDomain]) {
      domains[fullDomain] = {
        mainDomain: mainDomain,
        fullDomain: fullDomain,
        baseUrl: `https://${fullDomain}`,
        urls: []
      };
    }

    domains[fullDomain].urls.push(url);
  }

  // Traiter chaque domaine/sous-domaine s√©par√©ment
  const results = [];

  // Fonction pour formater le texte d'affichage d'une URL
  function formatDisplayText(segment) {
    if (!segment) return "HOME PAGE";
    // D√©codage des caract√®res URL (comme %20, %C3%A9, etc.)
    try {
      const decoded = decodeURIComponent(segment);
      return decoded.toUpperCase().replace(/-/g, ' ');
    } catch (e) {
      // En cas d'erreur de d√©codage, utiliser le segment tel quel
      return segment.toUpperCase().replace(/-/g, ' ');
    }
  }

  // Fonction pour extraire le chemin relatif d'une URL
  function getPathFromUrl(url, baseUrl) {
    // Supprimer le domaine
    let path = url.replace(baseUrl, '');

    // Supprimer les slashes au d√©but et √† la fin
    if (path.startsWith('/')) path = path.substring(1);
    if (path.endsWith('/')) path = path.substring(0, path.length - 1);

    return path;
  }

  // Fonction pour cr√©er l'arborescence d'un domaine sp√©cifique
  function processUrlsForDomain(domainInfo) {
    // Cr√©er une structure arborescente pour ce domaine
    const tree = {};

    // Ajouter la page d'accueil (niveau 0)
    tree[domainInfo.baseUrl] = {
      url: domainInfo.baseUrl,
      level: 0,
      segments: [],
      displayText: domainInfo.fullDomain.toUpperCase(),
      children: {}
    };

    // Trier les URLs par longueur de chemin (du plus court au plus long)
    domainInfo.urls.sort((a, b) => {
      const pathA = getPathFromUrl(a, domainInfo.baseUrl);
      const pathB = getPathFromUrl(b, domainInfo.baseUrl);

      const segmentsA = pathA ? pathA.split('/') : [];
      const segmentsB = pathB ? pathB.split('/') : [];

      // D'abord comparer le nombre de segments
      if (segmentsA.length !== segmentsB.length) {
        return segmentsA.length - segmentsB.length;
      }

      // Si m√™me nombre de segments, comparer alphab√©tiquement
      return pathA.localeCompare(pathB);
    });

    // Construire l'arborescence
    for (const url of domainInfo.urls) {
      // Ignorer l'URL racine d√©j√† ajout√©e
      if (url === domainInfo.baseUrl || url === domainInfo.baseUrl + '/') continue;

      const path = getPathFromUrl(url, domainInfo.baseUrl);
      const segments = path ? path.split('/') : [];

      // D√©terminer le niveau (limit√© √† 5)
      const level = Math.min(segments.length, 5);

      if (level === 0) continue; // Ignorer les duplications de l'URL racine

      // Construire le chemin complet segment par segment
      let currentNode = tree[domainInfo.baseUrl];
      let parentPath = domainInfo.baseUrl;

      for (let i = 0; i < level; i++) {
        const segment = segments[i];
        const currentPath = parentPath + '/' + segment;

        // Cr√©er le n≈ìud s'il n'existe pas
        if (!currentNode.children[segment]) {
          currentNode.children[segment] = {
            url: currentPath,
            level: i + 1,
            segments: segments.slice(0, i + 1),
            displayText: formatDisplayText(segment),
            children: {}
          };
        }

        // Avancer au n≈ìud enfant
        currentNode = currentNode.children[segment];
        parentPath = currentPath;
      }
    }

    // Convertir l'arborescence en lignes
    const domainRows = [];

    // Fonction r√©cursive pour parcourir l'arborescence
    function traverseTree(node) {
      // Cr√©er une nouvelle ligne
      const row = {
        "Niv 0": "",
        "Niv 1": "",
        "Niv 2": "",
        "Niv 3": "",
        "Niv 4": "",
        "Niv 5": "",
        "URL": node.url // Ajout de la colonne URL avec l'URL en texte brut
      };

      // D√©finir la valeur au niveau appropri√©
      if (node.level <= 5) {
        row[`Niv ${node.level}`] = `=HYPERLINK("${node.url}";"${node.displayText}")`;
      }

      // Ajouter la ligne au r√©sultat
      domainRows.push(row);

      // Traiter les enfants dans l'ordre alphab√©tique
      const children = Object.values(node.children);
      children.sort((a, b) => a.displayText.localeCompare(b.displayText));

      for (const child of children) {
        traverseTree(child);
      }
    }

    // Commencer le parcours avec le n≈ìud racine
    traverseTree(tree[domainInfo.baseUrl]);

    return domainRows;
  }

  // Trier les domaines: d'abord le domaine principal (sans sous-domaine), puis les sous-domaines
  const sortedDomains = Object.values(domains).sort((a, b) => {
    // Si un domaine est exactement le domaine principal, il vient en premier
    const aParts = a.fullDomain.split('.');
    const bParts = b.fullDomain.split('.');

    // Cas sp√©cial pour www: le traiter comme domaine principal
    const aIsWWW = aParts.length > 2 && aParts[0] === 'www';
    const bIsWWW = bParts.length > 2 && bParts[0] === 'www';

    if (aIsWWW && !bIsWWW) return -1;
    if (!aIsWWW && bIsWWW) return 1;

    // Ensuite comparer par nombre de parties
    if (aParts.length !== bParts.length) {
      return aParts.length - bParts.length;
    }

    // Enfin, comparer alphab√©tiquement
    return a.fullDomain.localeCompare(b.fullDomain);
  });

  // Traiter chaque domaine et ajouter les r√©sultats
  for (const domainInfo of sortedDomains) {
    const domainRows = processUrlsForDomain(domainInfo);
    results.push(...domainRows);
  }

  return results;
}

/**
 * Fonction principale pour traiter l'entr√©e de n8n
 */
function processInput() {
  try {
    // R√©cup√©rer les donn√©es de la node "Map a website and get urls" en utilisant la m√©thode $()
    // Cette m√©thode a √©t√© confirm√©e fonctionnelle par nos tests
    const firecrawlData = $('Map a website and get urls').item.json;

    // V√©rifier la structure des donn√©es
    if (!firecrawlData || !firecrawlData.success || !Array.isArray(firecrawlData.links)) {
      throw new Error("Donn√©es d'entr√©e non valides ou structure incorrecte");
    }

    // Traiter les URLs pour cr√©er l'arborescence
    const siteHierarchy = createSiteHierarchy(firecrawlData);

    // Cr√©er un nouvel item pour chaque ligne de l'arborescence
    // C'est le format attendu par Google Sheets dans n8n
    return siteHierarchy.map(row => {
      return {
        json: row
      };
    });

  } catch (error) {
    console.error("Erreur lors du traitement:", error.message);
    // Retourner un message d'erreur format√© pour n8n
    return [{
      json: {
        error: true,
        message: error.message,
        details: error.stack
      }
    }];
  }
}

// Ex√©cuter le traitement
return processInput();

