// Merge all incoming items (each with one binary photoX) into one item
const mergedItem = {
  json: {},
  binary: {}
};

for (const item of items) {
  // Copy every binary field from each item into mergedItem.binary
  for (const [key, bin] of Object.entries(item.binary || {})) {
    mergedItem.binary[key] = bin;
  }
}

// Return a single-item array
return [mergedItem];

return items.map((item, index) => {
  // Grab the existing binary buffer under "data"
  const buffer = item.binary.data;
  // Build a new item with the renamed binary key
  return {
    json: item.json,
    binary: {
      // Rename to photo1, photo2, ...
      [`photo${index + 1}`]: buffer
    }
  };
});

