// Content Analyzer - Analyzes post content for engagement and quality
const inputData = $input.all()[0].json;
const profile = inputData.result.profile || {};
const posts = inputData.result.recent_posts || [];

function analyzeContent(posts) {
  return posts.map(post => {
    const likes = parseInt(post.likes?.replace(/[^0-9]/g, '') || '0');
    const comments = parseInt(post.comments?.replace(/[^0-9]/g, '') || '0');
    const engagementRate = likes > 0 ? ((likes + comments) / likes * 100).toFixed(2) : 0;

    // Content quality scoring based on caption length, hashtags, mentions
    const captionScore = post.caption ? Math.min(post.caption.length / 100, 10) : 0;
    const hashtagScore = post.hashtags ? Math.min(post.hashtags.length * 2, 10) : 0;
    const mentionScore = post.mentions ? post.mentions.length * 5 : 0;
    const qualityScore = (captionScore + hashtagScore + mentionScore) / 3;

    return {
      ...post,
      engagement_rate: `${engagementRate}%`,
      content_quality_score: qualityScore.toFixed(1),
      performance_tier: engagementRate > 5 ? 'High' : engagementRate > 2 ? 'Medium' : 'Low'
    };
  });
}

const analyzedPosts = analyzeContent(posts);
const avgEngagement = analyzedPosts.length > 0 ? 
  (analyzedPosts.reduce((sum, post) => sum + parseFloat(post.engagement_rate), 0) / analyzedPosts.length).toFixed(2) : 0;

return [{
  json: {
    profile,
    analyzed_posts: analyzedPosts,
    analytics: {
      total_posts: posts.length,
      average_engagement: `${avgEngagement}%`,
      high_performing_posts: analyzedPosts.filter(p => p.performance_tier === 'High').length,
      analysis_date: new Date().toISOString()
    }
  }
}];

// Brand Mention Detector - Identifies brand mentions and sponsored content
const inputData = $input.all()[0].json;
const posts = inputData.analyzed_posts || [];
const profile = inputData.profile || {};

// Define brand keywords to monitor
const brandKeywords = [
  'nike', 'adidas', 'coca-cola', 'pepsi', 'apple', 'samsung',
  'sponsored', '#ad', '#sponsored', '#partnership', '#collab'
];

function detectBrandMentions(posts) {
  return posts.map(post => {
    const caption = (post.caption || '').toLowerCase();
    const hashtags = (post.hashtags || []).map(tag => tag.toLowerCase());
    const mentions = post.mentions || [];

    // Detect brand mentions in caption
    const brandMentionsInCaption = brandKeywords.filter(brand => 
      caption.includes(brand.toLowerCase())
    );

    // Detect sponsored content indicators
    const sponsoredIndicators = ['#ad', '#sponsored', '#partnership', '#collab'];
    const isSponsored = sponsoredIndicators.some(indicator => 
      caption.includes(indicator) || hashtags.includes(indicator)
    );

    // Extract mentioned brands from @ mentions
    const mentionedBrands = mentions.filter(mention => 
      brandKeywords.some(brand => mention.toLowerCase().includes(brand))
    );

    return {
      ...post,
      brand_mentions: brandMentionsInCaption,
      mentioned_brands: mentionedBrands,
      is_sponsored_content: isSponsored,
      brand_mention_count: brandMentionsInCaption.length + mentionedBrands.length
    };
  });
}

const postsWithBrands = detectBrandMentions(posts);
const sponsoredPosts = postsWithBrands.filter(post => post.is_sponsored_content);
const totalBrandMentions = postsWithBrands.reduce((sum, post) => sum + post.brand_mention_count, 0);

return [{
  json: {
    profile,
    analyzed_posts: postsWithBrands,
    brand_analytics: {
      total_brand_mentions: totalBrandMentions,
      sponsored_posts_count: sponsoredPosts.length,
      sponsored_content_ratio: posts.length > 0 ? (sponsoredPosts.length / posts.length * 100).toFixed(1) + '%' : '0%',
      top_mentioned_brands: [...new Set(postsWithBrands.flatMap(post => post.brand_mentions))],
      detection_date: new Date().toISOString()
    }
  }
}];

// Campaign Performance Tracker - Tracks campaign metrics and KPIs
const inputData = $input.all()[0].json;
const posts = inputData.analyzed_posts || [];
const profile = inputData.profile || {};
const brandAnalytics = inputData.brand_analytics || {};

function trackCampaignPerformance(posts, profile) {
  const sponsoredPosts = posts.filter(post => post.is_sponsored_content);

  // Calculate campaign metrics
  const totalReach = parseInt(profile.followers?.replace(/[^0-9]/g, '') || '0');
  const totalEngagement = posts.reduce((sum, post) => {
    const likes = parseInt(post.likes?.replace(/[^0-9]/g, '') || '0');
    const comments = parseInt(post.comments?.replace(/[^0-9]/g, '') || '0');
    return sum + likes + comments;
  }, 0);

  const sponsoredEngagement = sponsoredPosts.reduce((sum, post) => {
    const likes = parseInt(post.likes?.replace(/[^0-9]/g, '') || '0');
    const comments = parseInt(post.comments?.replace(/[^0-9]/g, '') || '0');
    return sum + likes + comments;
  }, 0);

  // Performance metrics
  const avgEngagementPerPost = posts.length > 0 ? (totalEngagement / posts.length).toFixed(0) : 0;
  const sponsoredAvgEngagement = sponsoredPosts.length > 0 ? (sponsoredEngagement / sponsoredPosts.length).toFixed(0) : 0;
  const engagementRate = totalReach > 0 ? (totalEngagement / totalReach * 100).toFixed(2) : 0;

  // Campaign performance score (0-100)
  const performanceScore = Math.min(
    (parseFloat(engagementRate) * 10) + 
    (sponsoredPosts.length * 5) + 
    (brandAnalytics.total_brand_mentions * 2), 
    100
  ).toFixed(1);

  return {
    campaign_metrics: {
      total_posts: posts.length,
      sponsored_posts: sponsoredPosts.length,
      total_reach: totalReach.toLocaleString(),
      total_engagement: totalEngagement.toLocaleString(),
      avg_engagement_per_post: avgEngagementPerPost,
      sponsored_avg_engagement: sponsoredAvgEngagement,
      overall_engagement_rate: `${engagementRate}%`,
      campaign_performance_score: performanceScore
    },
    top_performing_campaigns: sponsoredPosts
      .sort((a, b) => parseInt(b.likes) - parseInt(a.likes))
      .slice(0, 3)
      .map(post => ({
        post_url: post.post_url,
        brand_mentions: post.brand_mentions,
        engagement: `${post.likes} likes, ${post.comments} comments`,
        performance_tier: post.performance_tier
      }))
  };
}

const campaignData = trackCampaignPerformance(posts, profile);

return [{
  json: {
    profile,
    analyzed_posts: posts,
    brand_analytics: brandAnalytics,
    ...campaignData,
    tracking_date: new Date().toISOString()
  }
}];

// Marketing ROI Calculator - Calculates return on investment for campaigns
const inputData = $input.all()[0].json;
const campaignMetrics = inputData.campaign_metrics || {};
const profile = inputData.profile || {};
const sponsoredPosts = inputData.analyzed_posts?.filter(post => post.is_sponsored_content) || [];

// Estimated costs and values (these would typically come from your campaign data)
const estimatedCosts = {
  cost_per_sponsored_post: 500, // Average cost per sponsored post
  campaign_management_cost: 200, // Monthly management cost
  content_creation_cost: 100 // Per post content creation
};

function calculateROI(metrics, posts, costs) {
  // Calculate total investment
  const totalSponsoredPosts = parseInt(metrics.sponsored_posts || 0);
  const contentCosts = totalSponsoredPosts * costs.cost_per_sponsored_post;
  const managementCosts = costs.campaign_management_cost;
  const creationCosts = totalSponsoredPosts * costs.content_creation_cost;
  const totalInvestment = contentCosts + managementCosts + creationCosts;

  // Calculate estimated value/return
  const totalEngagement = parseInt(metrics.total_engagement?.replace(/[^0-9]/g, '') || '0');
  const reach = parseInt(metrics.total_reach?.replace(/[^0-9]/g, '') || '0');

  // Estimated value calculations
  const valuePerEngagement = 0.05; // $0.05 per engagement
  const valuePerReach = 0.001; // $0.001 per reach
  const engagementValue = totalEngagement * valuePerEngagement;
  const reachValue = reach * valuePerReach;
  const totalEstimatedValue = engagementValue + reachValue;

  // ROI calculation
  const roi = totalInvestment > 0 ? ((totalEstimatedValue - totalInvestment) / totalInvestment * 100).toFixed(1) : 0;
  const costPerEngagement = totalEngagement > 0 ? (totalInvestment / totalEngagement).toFixed(2) : 0;
  const costPerReach = reach > 0 ? (totalInvestment / reach * 1000).toFixed(2) : 0; // CPM

  return {
    roi_analysis: {
      total_investment: `$${totalInvestment.toLocaleString()}`,
      estimated_value: `$${totalEstimatedValue.toLocaleString()}`,
      roi_percentage: `${roi}%`,
      cost_per_engagement: `$${costPerEngagement}`,
      cost_per_thousand_reach: `$${costPerReach}`,
      investment_efficiency: roi > 0 ? 'Positive' : 'Needs Optimization',
      payback_period: roi > 0 ? 'Immediate' : 'Extended'
    },
    cost_breakdown: {
      sponsored_content_costs: `$${contentCosts.toLocaleString()}`,
      campaign_management_costs: `$${managementCosts.toLocaleString()}`,
      content_creation_costs: `$${creationCosts.toLocaleString()}`,
      total_costs: `$${totalInvestment.toLocaleString()}`
    },
    recommendations: [
      roi > 50 ? "Excellent ROI - Scale up campaigns" : 
      roi > 0 ? "Positive ROI - Continue with optimizations" : 
      "Negative ROI - Review targeting and content strategy",
      totalSponsoredPosts > 0 ? `Focus on top-performing content types` : "Increase sponsored content volume",
      parseFloat(costPerEngagement) > 1 ? "Optimize for lower cost per engagement" : "Good cost efficiency"
    ]
  };
}

const roiData = calculateROI(campaignMetrics, sponsoredPosts, estimatedCosts);

// Create final report
const finalReport = {
  influencer_profile: {
    username: profile.username,
    followers: profile.followers,
    verification_status: profile.verified ? 'Verified' : 'Not Verified'
  },
  campaign_summary: campaignMetrics,
  ...roiData,
  report_generated: new Date().toISOString(),
  next_review_date: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString() // 7 days from now
};

return [{
  json: finalReport
}];
