const input = $input.all().map(i => i.json);

// Collect store IDs
const stores = input.map(row => row.STORE);
// Collect items (from keys)
const items = Object.keys(input[0]).filter(k => k !== "STORE");

// Transpose
const result = items.map(ITEM => {
  let row = { ITEM };
  for (const store of stores) {
    const storeRow = input.find(r => r.STORE === store);
    row[store] = storeRow[ITEM];
  }
  return { json: row };
});

return result;

const agg = {};
for (const {json} of items) {
  const k = `${json.STORE}|${json.DAY}`;
  if (!agg[k]) agg[k] = { STORE: json.STORE, DAY: json.DAY, QTY: 0, TO: 0 };
  agg[k].QTY += Number(json.QTY||0);
  agg[k].TO  += Number(json.TO||0);
}
return Object.values(agg).map(r => ({ json: r }));

// Normalize + sort by turnover (TO) desc
const rows = items
  .map(i => ({
    ...i.json,
    TO: Number(i.json.TO || 0),   // turnover = QTY x Unit Price
    QTY: Number(i.json.QTY || 0),
  }))
  .sort((a, b) => b.TO - a.TO);

const n = rows.length;
const totalTO = rows.reduce((s, r) => s + r.TO, 0) || 1;

let cumTO = 0;
rows.forEach((r, idx) => {
  cumTO += r.TO;
  r.cum_turnover = cumTO;                 // absolute cumulative TO
  r.cum_share = +(cumTO / totalTO).toFixed(6); // 0..1
  r.sku_rank = idx + 1;
  r.cum_skus = +((idx + 1) / n).toFixed(6);    // 0..1
  r.cum_skus_pct = +(r.cum_skus * 100).toFixed(2); // 0..100 for charts
});

// Return table
return rows.map(r => ({ json: r }));

const agg = {};
for (const {json} of items) {
  const ITEM = String(json.ITEM);
  const TO = Number(json.TO);
  const QTY = Number(json.QTY);
  if (!agg[ITEM]) agg[ITEM] = { ITEM, TO: 0, QTY: 0 };
  agg[ITEM].TO += TO;
  agg[ITEM].QTY += QTY;
}
const rows = Object.values(agg).sort((a,b)=> b.TO - a.TO);
return rows.map(r => ({ json: r }));

// Build dictionaries of unique stores & skus
const stores = new Set();
const skus = new Set();
// Append dictionnaries with values
for (const {json} of items) { stores.add(json.STORE); skus.add(json.ITEM); }
// List of Unique SKU
const skuList = Array.from(skus).sort();

// Accumulate sums
const mat = {}; // store_id -> sku -> sum
for (const {json} of items) {
  const s = json.STORE;
  const k = json.ITEM;
  const v = Number(json.TO) || 0;
  if (!mat[s]) mat[s] = {};
  mat[s][k] = (mat[s][k] || 0) + v;
}

// Output wide rows with 0 fill
const rows = [];
for (const s of Array.from(stores).sort()) {
  const row = { STORE: s };
  for (const k of skuList) row[k] = mat[s][k] || 0;
  rows.push(row);
}
return rows.map(r => ({ json: r }));

// INPUT items: rows with fields ITEM, DAY, QTY (others optional)

function mean(a){ return a.reduce((s,x)=>s + x, 0) / (a.length || 1); }
function stdev_samp(a){
  if (a.length <= 1) return 0;
  const m = mean(a);
  const v = a.reduce((s,x)=> s + (x - m) ** 2, 0) / (a.length - 1);
  return Math.sqrt(v);
}

// 1) build daily series per ITEM (sum across stores)
const series = {};            // ITEM -> { day -> qty_sum }
let totalQty = 0;

for (const { json } of items) {
  const item = String(json.ITEM);
  const day  = String(json.DAY);
  const qty  = Number(json.QTY || 0);

  if (!series[item]) series[item] = {};
  series[item][day] = (series[item][day] || 0) + qty;
  totalQty += qty;
}

// 2) compute metrics per ITEM
const out = [];
for (const [item, dayMap] of Object.entries(series)) {
  const daily = Object.values(dayMap);                 // array of daily qty
  const qty_total = daily.reduce((s,x)=>s+x, 0);
  const m = mean(daily);
  const sd = stdev_samp(daily);
  const cv = m ? sd / m : null;
  const share_qty_pct = totalQty ? (qty_total / totalQty) * 100 : 0;

  out.push({
    ITEM: item,
    qty_total,
    share_qty_pct: Number(share_qty_pct.toFixed(2)),
    mean_qty: Number(m.toFixed(3)),
    std_qty: Number(sd.toFixed(3)),
    cv_qty: cv == null ? null : Number(cv.toFixed(3)),
  });
}

// Optional: sort by share desc
out.sort((a,b) => b.share_qty_pct - a.share_qty_pct);

// Return as n8n items
return out.map(r => ({ json: r }));

// 1) Extract and sort by sales contribution
let rows = items.map(i => i.json);
rows.sort((a, b) => b.share_qty_pct - a.share_qty_pct);

// 2) Compute cumulative contribution
let cum = 0;
for (let r of rows) {
  cum += r.share_qty_pct;

  // 3) Assign class based on cumulative %
  if (cum <= 5) {
    r.ABC = 'A';   // top 5%
  } else if (cum <= 20) {
    r.ABC = 'B';   // next 15%
  } else {
    r.ABC = 'C';   // rest
  }

  r.cum_share = Number(cum.toFixed(2));
}

// 4) Return enriched rows
return rows.map(r => ({ json: r }));
