const output = $input.first().json.output;
let raw = output.result || output.completion || output.text || JSON.stringify(output);

// Step 1: Remove backticks if present
raw = raw.trim();
if (raw.startsWith("```json")) {
  raw = raw.replace(/^```json\s*/, '').replace(/```$/, '').trim();
} else if (raw.startsWith("```")) {
  raw = raw.replace(/^```\s*/, '').replace(/```$/, '').trim();
}

// Step 2: Find full JSON block from first { to last }
const start = raw.indexOf('{');
const end = raw.lastIndexOf('}');
if (start === -1 || end === -1 || end <= start) {
  return [{
    json: {
      error: "No valid JSON block found",
      raw_output: raw
    }
  }];
}

let jsonCandidate = raw.substring(start, end + 1).trim();

// Step 3: Unescape characters
jsonCandidate = jsonCandidate
  .replace(/\\n/g, '')
  .replace(/\\t/g, '')
  .replace(/\\"/g, '"')
  .replace(/\\'/g, "'")
  .replace(/\\\\/g, '\\');

// Step 4: Parse JSON safely
try {
  let parsed = JSON.parse(jsonCandidate);

  // Only double-parse if it looks like stringified JSON
  if (typeof parsed === "string" && parsed.trim().startsWith('{') && parsed.trim().endsWith('}')) {
    parsed = JSON.parse(parsed);
  }

  return [{ json: parsed }];
} catch (e) {
  return [{
    json: {
      error: "JSON parsing failed",
      raw_output: raw,
      attempted_extraction: jsonCandidate,
      message: e.message
    }
  }];
}

// --- Helper: Levenshtein Distance ---
function levenshtein(a, b) {
  const matrix = Array.from({ length: b.length + 1 }, (_, i) => [i]);
  for (let j = 0; j <= a.length; j++) matrix[0][j] = j;

  for (let i = 1; i <= b.length; i++) {
    for (let j = 1; j <= a.length; j++) {
      const cost = a[j - 1] === b[i - 1] ? 0 : 1;
      matrix[i][j] = Math.min(
        matrix[i - 1][j] + 1,
        matrix[i][j - 1] + 1,
        matrix[i - 1][j - 1] + cost
      );
    }
  }
  return matrix[b.length][a.length];
}

// --- Helper: Clean String ---
function cleanString(str) {
  if (str && typeof str === 'string') {
    return str
      .replace(/(^"|"$|\[|\])/g, '') // remove unwanted chars
      .replace(/\s{2,}/g, ' ')       // replace multiple spaces with one
      .toLowerCase()
      .trim();
  }
  return '';
}

// --- 1. Get all SKUs from "Split Out" and corresponding Unique Keys ---
const splitOutItems = $items('Split Out');
const uniqueKeyItems = $items('Generate Unique Key');

const inputSKUs = splitOutItems.map((item, index) => {
  const uniqueKeyItem = uniqueKeyItems[index];

  const code = cleanString(item.json.code);
  const description = cleanString(item.json.description);
  // handle both possible keys for last character
  const lastCharacter = cleanString(item.json['last_character'] ?? item.json['last character'] ?? '');

  let sku = `${code} ${description}`;

  if (lastCharacter) {
    if (/^[a-z0-9]+$/i.test(lastCharacter)) {
      // Append directly without space if alphanumeric postfix
      sku += lastCharacter;
    } else {
      // Otherwise, add a space
      sku += ` ${lastCharacter}`;
    }
  }

  sku = sku.trim().replace(/\s{2,}/g, ' ');

  return {
    Unique_Key: uniqueKeyItem ? uniqueKeyItem.json['Unique Key'] || uniqueKeyItem.json.Unique_Key || '' : '',
    serial_no: item.json['Serial No.'] ?? item.json['Serial_No.'] ?? '',
    sku,
    quantity: item.json.quantity,
    unit_price: item.json.unit_price,
    line_total: item.json.line_total,
    hsn_code: item.json.hsn_code,
    cgst: item.json.cgst,
    sgst: item.json.sgst
  };
});

// --- 2. Get all rows from "Google Sheets" ---
const sheetRows = $items('Fetch Master Data').map(i => i.json);

// --- 3. Set Levenshtein match threshold ---
const threshold = 5;
const results = [];

for (const inputSKU of inputSKUs) {
  let bestMatch = null;
  let lowestDistance = Infinity;

  for (const row of sheetRows) {
    const descriptionRaw = row['Jumax PDF Description'] || '';
    const description = cleanString(descriptionRaw);
    if (!description) continue;

    const distance = levenshtein(inputSKU.sku, description);

    if (distance < lowestDistance) {
      lowestDistance = distance;
      bestMatch = row;
    }
  }

  if (bestMatch && lowestDistance <= threshold) {
    const jumaxASIN = cleanString(bestMatch['ASIN']);
    const tASIN = cleanString(bestMatch['TWW ASIN']);
    const cost = Number(bestMatch['Cost '] || bestMatch['Cost'] || bestMatch['Rate']);
    const unitPrice = Number(inputSKU.unit_price);

    const asinMatch = jumaxASIN === tASIN;
    const costMatch = cost === unitPrice;
    const allConditionsMet = asinMatch && costMatch;

    results.push({
      json: {
        Unique_Key: inputSKU.Unique_Key,
        serial_no: inputSKU.serial_no,
        matchedSKU: inputSKU.sku,
        matchedRow: bestMatch,
        distance: lowestDistance,
        Jumax_ASIN: jumaxASIN,
        T_ASIN: tASIN,
        asinMatch,
        costMatch,
        quantity: inputSKU.quantity,
        unit_price: inputSKU.unit_price,
        costFromSheet: cost,
        line_total: inputSKU.line_total,
        hsn_code: inputSKU.hsn_code,
        cgst: inputSKU.cgst,
        sgst: inputSKU.sgst,
        message: allConditionsMet
          ? '✅ Match found, ASINs match, and Cost matches Unit Price'
          : asinMatch
            ? '⚠️ ASINs match but Cost does not match Unit Price'
            : '❌ Match found but ASINs do not match',
        All_Conditions_Met: allConditionsMet
      }
    });
  } else {
    results.push({
      json: {
        Unique_Key: inputSKU.Unique_Key,
        serial_no: inputSKU.serial_no,
        matchedSKU: inputSKU.sku,
        matchedRow: null,
        message: `❌ No match found for SKU: ${inputSKU.sku}`,
        closestDistance: lowestDistance,
        quantity: inputSKU.quantity,
        unit_price: inputSKU.unit_price,
        line_total: inputSKU.line_total,
        hsn_code: inputSKU.hsn_code,
        cgst: inputSKU.cgst,
        sgst: inputSKU.sgst,
        All_Conditions_Met: false
      }
    });
  }
}

// --- 4. Return final results ---
return results;
