// Fix for parsing AI email output
try {
    const output = $input.first().json.output;

    // Remove the ```json\n prefix and ```\n suffix if present
    let cleanOutput = output;
    if (output.startsWith('```json\n')) {
        cleanOutput = output.replace(/^```json\n/, '').replace(/\n```$/, '');
    }

    // Parse the JSON
    const parsed = JSON.parse(cleanOutput);

    // Return the email details as separate fields
    return {
        email: parsed.email || '',
        subject: parsed.subject || '',
        content: parsed.message || parsed.content || ''
    };

} catch (error) {
    // If JSON parsing fails, try to extract info using regex from the raw output
    const output = $input.first().json.output;

    const emailMatch = output.match(/"email":\s*"([^"]*)"/);
    const subjectMatch = output.match(/"subject":\s*"([^"]*)"/);
    const messageMatch = output.match(/"message":\s*"([^"]*(?:\\.[^"]*)*)"/);

    return {
        email: emailMatch ? emailMatch[1] : '',
        subject: subjectMatch ? subjectMatch[1] : '',
        content: messageMatch ? messageMatch[1].replace(/\\n/g, '\n').replace(/\\"/g, '"') : ''
    };
}
