parameters.text:
=You are a content strategist for LinkedIn.
Given a niche and audience, return 10 scroll-stopping post ideas as compact titles.
Output STRICT JSON:
{
  "ideas": [
    {"id": 1, "title": "..."},
    ...
    {"id": 10, "title": "..."}
  ]
}
Niche: {{$json.niche}}
Audience: {{$json.audience}}

parameters.jsCode:
// 1) Get raw AI output from the previous node
let raw = $input.first().json.output || $json.data || "";

// 2) Remove ```json fences if present
raw = raw.replace(/```json/i, "").replace(/```/g, "").trim();

// 3) Parse
let data;
try { data = JSON.parse(raw); }
catch (e) { throw new Error("Gemini did not return valid JSON:\n" + raw); }

const ideas = data.ideas || [];

// 4) Create correlationId for this email thread/session
const correlationId = Date.now().toString() + '-' + Math.random().toString(36).slice(2,8);

// 5) Build a nice email body
let body = `Here are 10 LinkedIn post ideas for “$('Edit Fields').first().json.audience” in “$('Edit Fields').first().json.niche”:\n\n`;
ideas.forEach(i => { body += `${i.id}. ${i.title}\n`; });
body += `\nReply with the number (1–10) to draft posts.\n`;

// 6) Return
return [{
  correlationId,
  niche: $('Edit Fields').first().json.niche,
  audience: $('Edit Fields').first().json.audience,
  ideas,
  emailBody: body
}];

// Pull a plain text we can regex against (snippet is usually fine)
const text = ($input.first().json.snippet || '').trim();

// First number in the reply
const numMatch = text.match(/\d+/);
const selectedNumber = numMatch ? Number(numMatch[0]) : null;

// Extract correlationId from Subject header
let subject = $input.first().json.Subject
const cidMatch = subject.match(/\[CID:\s*([^\]]+)\]/i);
const correlationId = cidMatch ? cidMatch[1] : null;

// Who replied (optional, can be useful)
let from = '';
if ($json.payload && $json.payload.headers) {
  const f = $json.payload.headers.find(h => h.name === 'From');
  from = f ? f.value : '';
}

if (!correlationId || !selectedNumber) {
  return [{ kind: 'unknown', reason: 'Missing CID or number', subject, text }];
}

return [{
  kind: 'numberReply',
  selectedNumber,
  correlationId,
  from
}];

// Parse ideas into array
const ideas = JSON.parse($input.first().json.ideas || "[]");

// Convert selected number to string because ids are strings in your JSON
const selectedNumber = $('Code1').first().json.selectedNumber;

// Find chosen idea
const chosen = ideas.find(i => i.id === selectedNumber);

if (!chosen) {
  return [{
    error: true,
    msg: 'Idea not found',
    selectedNumber
  }];
}

return [{
  correlationId: $input.first().json['correlationId '],
  selectedTopic: chosen.title,
  niche: $input.first().json.niche,
  audience: $input.first().json.audience
}];

parameters.text:
=Create 3 LinkedIn post drafts about the topic below, tailored to the audience.
Length: 120–200 words each, plain text. Avoid emojis unless natural.
Output strict JSON:
{
  "drafts": [
    {"id": 1, "text": "..."},
    {"id": 2, "text": "..."},
    {"id": 3, "text": "..."}
  ]
}
Topic: {{$json.selectedTopic}}
Audience: {{$json.audience}}
Niche: {{$json.niche}}
Tone: insightful, practical, founder-friendly

parameters.jsCode:
let raw = $input.first().json.output || $json.data || "";
raw = raw.replace(/```json/i, "").replace(/```/g, "").trim();

let data;
try { data = JSON.parse(raw); }
catch(e) { throw new Error("Gemini drafts JSON parse error:\n" + raw); }

const drafts = data.drafts || [];
let body = `Here are 3 drafts for:\n“$('Code2').first().json.selectedTopic”\n\n`;
drafts.forEach(d => { body += `${d.id}. ${d.text}\n\n`; });
body += `Reply with 1, 2, or 3 to publish on LinkedIn.\n`;

return [{
  correlationId: $('Code2').first().json.correlationId,
  selectedTopic: $('Code2').first().json.selectedTopic,
  drafts,
  emailBody: body
}];

// Extract drafts from sheet row
let drafts = $input.first().json.drafts || [];
if (typeof drafts === 'string') {
  try {
    drafts = JSON.parse(drafts);
  } catch (e) {
    return [{ error: true, msg: 'Failed to parse drafts JSON', raw: drafts }];
  }
}

// Get selected number from earlier node
const idx = $item(0).$node["Code1"].json.selectedNumber;
const selected = drafts.find(d => Number(d.id) === Number(idx));

if (!selected) {
  return [{
    error: true,
    msg: `Draft #${idx} not found`,
    availableDrafts: drafts.map(d => d.id)
  }];
}

return [{
  correlationId: $json.correlationId,
  selectedTopic: $json.selectedTopic,
  finalPost: selected.text
}];
