const inputData = $input.all();
const results = [];

for (let i = 0; i < inputData.length; i++) {
  try {

    let aiResponse = inputData[i].json.output || inputData[i].json.text || inputData[i].json.content || '';

    if (!aiResponse) {
      console.log(`アイテム ${i}: 空の応答をスキップ`);
      continue;
    }

    // 文字列の前後の空白を削除
    aiResponse = aiResponse.trim();

    // JSONの前後にある不要なテキストを削除
    // ```json や ``` で囲まれている場合の処理
    if (aiResponse.includes('```json')) {
      const jsonStart = aiResponse.indexOf('```json') + 7;
      const jsonEnd = aiResponse.indexOf('```', jsonStart);
      if (jsonEnd !== -1) {
        aiResponse = aiResponse.substring(jsonStart, jsonEnd).trim();
      }
    } else if (aiResponse.includes('```')) {
      const jsonStart = aiResponse.indexOf('```') + 3;
      const jsonEnd = aiResponse.indexOf('```', jsonStart);
      if (jsonEnd !== -1) {
        aiResponse = aiResponse.substring(jsonStart, jsonEnd).trim();
      }
    }

    // JSONの開始と終了を見つける
    const jsonStartIndex = aiResponse.indexOf('[');
    const jsonEndIndex = aiResponse.lastIndexOf(']');

    if (jsonStartIndex !== -1 && jsonEndIndex !== -1 && jsonEndIndex > jsonStartIndex) {
      const jsonString = aiResponse.substring(jsonStartIndex, jsonEndIndex + 1);

      try {
        // JSONをパース
        const parsedData = JSON.parse(jsonString);

        // 配列でない場合は配列に変換
        const dataArray = Array.isArray(parsedData) ? parsedData : [parsedData];

        // 各アイテムを処理
        dataArray.forEach((item, index) => {
          // 必要なフィールドが存在することを確認
          if (item && typeof item === 'object') {
            const processedItem = {
              dishName: item.dishName || item.dish_name || item.name || 'Unknown',
              calories: item.calories || item.calorie || item.cal || 0,
              // 元のデータも保持（デバッグ用）
              originalIndex: i,
              itemIndex: index,
              timestamp: new Date().toISOString()
            };

            // カロリーが数値でない場合は数値に変換を試行
            if (typeof processedItem.calories === 'string') {
              const numericCalories = parseInt(processedItem.calories.replace(/[^\d]/g, ''));
              processedItem.calories = isNaN(numericCalories) ? 0 : numericCalories;
            }

            results.push({ json: processedItem });
          }
        });

      } catch (parseError) {
        console.log(`アイテム ${i}: JSON解析エラー - ${parseError.message}`);
        console.log(`問題のあるJSON文字列: ${jsonString}`);

        // JSONが無効な場合でも、手動でデータを抽出を試行
        const manualExtract = extractDataManually(aiResponse);
        if (manualExtract) {
          results.push({ json: manualExtract });
        }
      }
    } else {
      console.log(`アイテム ${i}: 有効なJSON構造が見つかりません`);

      // 手動でデータを抽出を試行
      const manualExtract = extractDataManually(aiResponse);
      if (manualExtract) {
        results.push({ json: manualExtract });
      }
    }

  } catch (error) {
    console.log(`アイテム ${i}: 処理エラー - ${error.message}`);

    // エラーが発生した場合でもデフォルト値で処理を継続
    results.push({
      json: {
        dishName: 'Error',
        calories: 0,
        error: error.message,
        originalIndex: i,
        timestamp: new Date().toISOString()
      }
    });
  }
}

// 手動でデータを抽出する関数
function extractDataManually(text) {
  try {
    const dishMatch = text.match(/"dishName"\s*:\s*"([^"]+)"/i) || 
                     text.match(/"dish_name"\s*:\s*"([^"]+)"/i) ||
                     text.match(/"name"\s*:\s*"([^"]+)"/i);

    const caloriesMatch = text.match(/"calories"\s*:\s*"?(\d+)"?/i) ||
                         text.match(/"calorie"\s*:\s*"?(\d+)"?/i) ||
                         text.match(/"cal"\s*:\s*"?(\d+)"?/i);

    if (dishMatch || caloriesMatch) {
      return {
        dishName: dishMatch ? dishMatch[1] : 'Unknown',
        calories: caloriesMatch ? parseInt(caloriesMatch[1]) : 0,
        extractedManually: true,
        timestamp: new Date().toISOString()
      };
    }
  } catch (error) {
    console.log('手動抽出エラー:', error.message);
  }
  return null;
}

// 結果が空の場合のデフォルト処理
if (results.length === 0) {
  console.log('処理可能なデータが見つかりませんでした');
  results.push({
    json: {
      dishName: 'No Data',
      calories: 0,
      error: 'No valid data found',
      timestamp: new Date().toISOString()
    }
  });
}

console.log(`処理完了: ${results.length} 件のアイテムを変換しました`);
return results;

