const inputData = $input.all();
const results = [];

for (let i = 0; i < inputData.length; i++) {
  try {
    // AIエージェントからの出力を取得
    let aiResponse = inputData[i].json.output || inputData[i].json.text || inputData[i].json.content || '';

    // 空の場合はスキップ
    if (!aiResponse) {
      console.log(`アイテム ${i}: 空の応答をスキップ`);
      continue;
    }

    // 文字列の前後の空白を削除
    aiResponse = aiResponse.trim();

    // JSONデータを抽出する
    const menuItems = extractMenuData(aiResponse);

    if (menuItems && menuItems.length > 0) {
      // LINEメッセージを生成
      const lineMessage = generateLineMessage(menuItems);

      results.push({
        json: {
          message: lineMessage,
          menuCount: menuItems.length,
          totalCalories: menuItems.reduce((sum, item) => sum + item.calories, 0),
          timestamp: new Date().toISOString(),
          originalIndex: i
        }
      });

      console.log(`アイテム ${i}: ${menuItems.length}個のメニューを処理しました`);
    } else {
      // 食品が検出されなかった場合
      results.push({
        json: {
          message: "画像から食品を検出できませんでした。",
          menuCount: 0,
          totalCalories: 0,
          timestamp: new Date().toISOString(),
          originalIndex: i
        }
      });

      console.log(`アイテム ${i}: 食品が検出されませんでした`);
    }

  } catch (error) {
    console.log(`アイテム ${i}: 処理エラー - ${error.message}`);

    // エラーが発生した場合のデフォルトメッセージ
    results.push({
      json: {
        message: "画像の解析でエラーが発生しました。もう一度お試しください。",
        menuCount: 0,
        totalCalories: 0,
        error: error.message,
        timestamp: new Date().toISOString(),
        originalIndex: i
      }
    });
  }
}

// JSONデータを抽出する関数
function extractMenuData(text) {
  const menuItems = [];

  try {
    // JSONの前後にある不要なテキストを削除
    let cleanText = text;

    // ```json や ``` で囲まれている場合の処理
    if (cleanText.includes('```json')) {
      const jsonStart = cleanText.indexOf('```json') + 7;
      const jsonEnd = cleanText.indexOf('```', jsonStart);
      if (jsonEnd !== -1) {
        cleanText = cleanText.substring(jsonStart, jsonEnd).trim();
      }
    } else if (cleanText.includes('```')) {
      const jsonStart = cleanText.indexOf('```') + 3;
      const jsonEnd = cleanText.indexOf('```', jsonStart);
      if (jsonEnd !== -1) {
        cleanText = cleanText.substring(jsonStart, jsonEnd).trim();
      }
    }

    // JSONの開始と終了を見つける
    const jsonStartIndex = cleanText.indexOf('[');
    const jsonEndIndex = cleanText.lastIndexOf(']');

    if (jsonStartIndex !== -1 && jsonEndIndex !== -1 && jsonEndIndex > jsonStartIndex) {
      const jsonString = cleanText.substring(jsonStartIndex, jsonEndIndex + 1);

      try {
        // JSONをパース
        const parsedData = JSON.parse(jsonString);

        // 配列でない場合は配列に変換
        const dataArray = Array.isArray(parsedData) ? parsedData : [parsedData];

        // 各アイテムを処理
        dataArray.forEach((item) => {
          if (item && typeof item === 'object') {
            const dishName = item.dishName || item.dish_name || item.name || 'Unknown';
            let calories = item.calories || item.calorie || item.cal || 0;

            // カロリーが文字列の場合は数値に変換
            if (typeof calories === 'string') {
              const numericCalories = parseInt(calories.replace(/[^\d]/g, ''));
              calories = isNaN(numericCalories) ? 0 : numericCalories;
            }

            // 有効なデータのみ追加
            if (dishName !== 'Unknown' && calories > 0) {
              menuItems.push({
                dishName: dishName,
                calories: calories
              });
            }
          }
        });

      } catch (parseError) {
        console.log('JSON解析エラー:', parseError.message);
        // 手動でデータを抽出を試行
        const manualExtract = extractDataManually(text);
        if (manualExtract) {
          menuItems.push(manualExtract);
        }
      }
    } else {
      // 手動でデータを抽出を試行
      const manualExtract = extractDataManually(text);
      if (manualExtract) {
        menuItems.push(manualExtract);
      }
    }

  } catch (error) {
    console.log('データ抽出エラー:', error.message);
  }

  return menuItems;
}

// 手動でデータを抽出する関数
function extractDataManually(text) {
  try {
    const dishMatch = text.match(/"dishName"\s*:\s*"([^"]+)"/i) || 
                     text.match(/"dish_name"\s*:\s*"([^"]+)"/i) ||
                     text.match(/"name"\s*:\s*"([^"]+)"/i);

    const caloriesMatch = text.match(/"calories"\s*:\s*"?(\d+)"?/i) ||
                         text.match(/"calorie"\s*:\s*"?(\d+)"?/i) ||
                         text.match(/"cal"\s*:\s*"?(\d+)"?/i);

    if (dishMatch && caloriesMatch) {
      const calories = parseInt(caloriesMatch[1]);
      if (!isNaN(calories) && calories > 0) {
        return {
          dishName: dishMatch[1],
          calories: calories
        };
      }
    }
  } catch (error) {
    console.log('手動抽出エラー:', error.message);
  }
  return null;
}

// LINEメッセージを生成する関数
function generateLineMessage(menuItems) {
  if (!menuItems || menuItems.length === 0) {
    return "画像から食品を検出できませんでした。";
  }

  let message = "カロリー計算：\n";

  // 各メニューアイテムを追加
  menuItems.forEach((item) => {
    message += `・${item.dishName}(${item.calories}kcal)\n`;
  });

  message += "シートに記入しました";

  return message;
}

