for (const item of items) { // 'items' here represents all rows fetched from your Google Sheet
  const mac = item.json.mac;
  const time = new Date(item.json.timestampISO); // Converts the timestamp ISO string to a Date object

  if (!mac || isNaN(time)) continue; // Skips if MAC is missing or time is invalid

  // This is the crucial part for finding the LATEST status
  if (!latestByMac[mac] || new Date(latestByMac[mac].json.timestampISO) < time) {
    latestByMac[mac] = item; // If this MAC is new, or the current 'item' is newer than what's stored, update it
  }
}

// Filter the latest per MAC that are still disconnected and have passed their 30-minute threshold
const overdueAlerts = Object.values(latestByMac).filter(item => {
