  // and we want to trigger if that condition is met internally, irrespective of 'now'.
  const isOverdueFinal = !isNaN(checkAfter.getTime());

  const notAlerted = !(alertSent === true || alertSent === "true");

return (status === 'disconnected' || status === 'isolated') && isOverdueFinal && notAlerted;
