const results = [];

for (const item of $input.all()) {
  try {
    const rawData = item.json.output; // The raw string from the LLM

    // --- FIX IS HERE ---
    // Find the first opening curly brace '{'
    const firstBrace = rawData.indexOf('{');
    // Find the last closing curly brace '}'
    const lastBrace = rawData.lastIndexOf('}');

    // If both braces are found, extract the content between them
    if (firstBrace !== -1 && lastBrace !== -1) {
      const jsonString = rawData.substring(firstBrace, lastBrace + 1);

      // Now, parse the cleaned string
      const obj = JSON.parse(jsonString);

      results.push({ json: obj });
    }
    // --- END OF FIX ---

  } catch (error) {
    // This will catch any items that still fail to parse,
    // so the whole workflow doesn't stop for one bad item.
    console.error("Failed to parse an item:", error);
  }
}

return results;
