const raw = $json["output"];

// Step 1: Clean and format the structure
let cleaned = raw
  .replace(/\*\*/g, '')               // Remove bold markers
  .replace(/\\n/g, '\n')              // Replace literal \n with real newlines
  .replace(/\* /g, '- ')              // Convert bullets from * to -
  .replace(/\*/g, '')                 // Remove stray asterisks
  .replace(/_/g, '')                  // Remove underscores
  .replace(/`/g, '')                  // Remove backticks
  .replace(/>/g, '')                  // Remove blockquote symbols
  .replace(/#/g, '');                 // Remove markdown headers

// Step 2: Normalize spacing and extra newlines
cleaned = cleaned
  .replace(/\n{3,}/g, '\n\n')         // Collapse 3+ newlines to 2
  .replace(/[ ]{2,}/g, ' ')           // Collapse multiple spaces
  .trim();

return [{
  cleanedMessage: cleaned
}];
