options.customCss:
:root {
  /* Colors */
  --chat--color-primary: #e74266; /* leave as accent or swap if desired */
  --chat--color-secondary: #A3CEF1;       /* pastel blue */
  --chat--color-secondary-shade-50: #8EBEE8;
  --chat--color-secondary-shade-100: #78ADDE;
  --chat--color-white: #ffffff;
  --chat--color-light: #f7f9fb;
  --chat--color-light-shade-50: #eef1f5;
  --chat--color-medium: #d2d4d9;
  --chat--color-dark: #101330;
  --chat--color-disabled: #a0a4ab;
  --chat--color-typing: #404040;

  /* Base Layout */
  --chat--spacing: 1rem;
  --chat--border-radius: 0.2rem;
  --chat--transition-duration: 0.15s;

  /* Window Dimensions */
  --chat--window--width: 400px;
  --chat--window--height: 600px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid var(--chat--color-light-shade-50);
  --chat--window--border-radius: var(--chat--border-radius);
  --chat--window--margin-bottom: var(--chat--spacing);

  /* Header Styles */
  --chat--header--background: var(--chat--color-white);
  --chat--header--color: var(--chat--color-dark);
  --chat--header--padding: calc(var(--chat--spacing) * 0.75);
  --chat--header-height: auto;
  --chat--header--border-bottom: 1px solid var(--chat--color-light-shade-50);

  /* Message Styles */
  --chat--message--font-size: 1rem;
  --chat--message--padding: calc(var(--chat--spacing) * 0.75);
  --chat--message--border-radius: var(--chat--border-radius);
  --chat--message-line-height: 1.5;
  --chat--message--margin-bottom: var(--chat--spacing);
  --chat--message--bot--background: var(--chat--color-white);
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--user--background: var(--chat--color-secondary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--pre--background: rgba(0, 0, 0, 0.03);

  /* Toggle Button */
  --chat--toggle--background: var(--chat--color-secondary);
  --chat--toggle--hover--background: var(--chat--color-secondary-shade-50);
  --chat--toggle--active--background: var(--chat--color-secondary-shade-100);

  /* Input Area */
  --chat--textarea--height: 50px;
  --chat--textarea--max-height: 30rem;
  --chat--input--background: var(--chat--color-white);
  --chat--input--color: var(--chat--color-dark);
  --chat--input--padding: 0.8rem;
  --chat--input--border: 1px solid var(--chat--color-light-shade-50);
  --chat--input--border-active: 1px solid var(--chat--color-secondary-shade-100);
  --chat--input--border-radius: var(--chat--border-radius);

  /* Button Styles */
  --chat--button--background: var(--chat--color-secondary);
  --chat--button--color: var(--chat--color-white);
  --chat--button--padding: 0.5rem var(--chat--spacing);
  --chat--button--border-radius: var(--chat--border-radius);
  --chat--button--hover--background: var(--chat--color-secondary-shade-50);

  /* Send & File Buttons */
  --chat--input--send--button--background: var(--chat--color-white);
  --chat--input--send--button--color: var(--chat--color-secondary);
  --chat--input--file--button--color: var(--chat--color-secondary);
  --chat--files-spacing: 0.25rem;

  /* Body & Footer */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-light);
  --chat--footer--color: var(--chat--color-dark);
}

.chat-message {
  max-width: 50%;
}
