const attachmentsArr = Object.values($input.item.binary);

const output = [];
for (const attachment of attachmentsArr) {
  if (attachment.mimeType === 'application/pdf') {
    output.push({
      json: {
        fileName: attachment.fileName
      },
      binary: {
        data: attachment
      }
    });
  }
}

return output;
