// Get all items from the form upload
const items = $input.all();
const outputItems = [];

// Process each item
for (const item of items) {
  const binaryData = item.binary || {};

  // Extract all file properties (Files_0, Files_1, etc.)
  const fileKeys = Object.keys(binaryData).filter(key => key.startsWith('Files_'));

  // Create separate items for each file
  for (const fileKey of fileKeys) {
    const file = binaryData[fileKey];
    outputItems.push({
      json: {
        fileName: file.fileName,
        mimeType: file.mimeType,
        fileSize: file.fileSize,
        fileExtension: file.fileExtension
      },
      binary: {
        data: file
      }
    });
  }
}

return outputItems;
